//Setacustomsizeforthissection'spages.
builder->get_PageSetup()->set_PageWidth(620);
builder->get_PageSetup()->set_PageHeight(480);

ASSERT_EQ(PaperSize::Custom,builder->get_PageSetup()->get_PaperSize());

builder->Writeln(String::Format(u"Thispageis{0}x{1}.",builder->get_PageSetup()->get_PageWidth(),builder->get_PageSetup()->get_PageHeight()));

