Examples


Shows how to apply and revert page setup settings to sections in a document. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

//Modifythepagesetuppropertiesforthebuilder'scurrentsectionandaddtext.
builder->get_PageSetup()->set_Orientation(Orientation::Landscape);
builder->get_PageSetup()->set_VerticalAlignment(PageVerticalAlignment::Center);
builder->Writeln(u"Thisisthefirstsection,whichlandscapeorientedwithverticallycenteredtext.");

//Ifwestartanewsectionusingadocumentbuilder,
//itwillinheritthebuilder'scurrentpagesetupproperties.
builder->InsertBreak(BreakType::SectionBreakNewPage);

ASSERT_EQ(Orientation::Landscape,doc->get_Sections()->idx_get(1)->get_PageSetup()->get_Orientation());
ASSERT_EQ(PageVerticalAlignment::Center,doc->get_Sections()->idx_get(1)->get_PageSetup()->get_VerticalAlignment());

//Wecanrevertitspagesetuppropertiestotheirdefaultvaluesusingthe"ClearFormatting"method.
builder->get_PageSetup()->ClearFormatting();

ASSERT_EQ(Orientation::Portrait,doc->get_Sections()->idx_get(1)->get_PageSetup()->get_Orientation());
ASSERT_EQ(PageVerticalAlignment::Top,doc->get_Sections()->idx_get(1)->get_PageSetup()->get_VerticalAlignment());

builder->Writeln(u"Thisisthesecondsection,whichisindefaultLetterpapersize,portraitorientationandtopalignment.");

doc->Save(ArtifactsDir+u"PageSetup.ClearFormatting.docx");

