doc->get_CustomDocumentProperties()->Add(u"AuthorizationDate",System::DateTime::get_Now());

std::cout<<"Documentauthorizedon"<<doc->get_CustomDocumentProperties()->idx_get(u"AuthorizationDate")->ToDateTime()<<std::endl;

