Examples


Shows how to use a callback to save a document to HTML page by page. voidPageFileNames()
{
autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

builder->Writeln(u"Page1.");
builder->InsertBreak(BreakType::PageBreak);
builder->Writeln(u"Page2.");
builder->InsertImage(ImageDir+u"Logo.jpg");
builder->InsertBreak(BreakType::PageBreak);
builder->Writeln(u"Page3.");

//Createan"HtmlFixedSaveOptions"object,whichwecanpasstothedocument's"Save"method
//tomodifyhowweconvertthedocumenttoHTML.
autohtmlFixedSaveOptions=MakeObject<HtmlFixedSaveOptions>();

//WewillsaveeachpageinthisdocumenttoaseparateHTMLfileinthelocalfilesystem.
//SetacallbackthatallowsustonameeachoutputHTMLdocument.
htmlFixedSaveOptions->set_PageSavingCallback(MakeObject<ExSavingCallback::CustomFileNamePageSavingCallback>());

doc->Save(ArtifactsDir+u"SavingCallback.PageFileNames.html",htmlFixedSaveOptions);

ArrayPtr<String>filePaths=System::IO::Directory::GetFiles(ArtifactsDir)
->LINQ_Where([](Strings){returns.StartsWith(ArtifactsDir+u"SavingCallback.PageFileNames.Page_");})
->LINQ_ToArray();

ASSERT_EQ(3,filePaths->get_Length());
}

classCustomFileNamePageSavingCallback:publicIPageSavingCallback
{
public:
voidPageSaving(SharedPtr<PageSavingArgs>args)override
{
StringoutFileName=String::Format(u"{0}SavingCallback.PageFileNames.Page_{1}.html",ArtifactsDir,args->get_PageIndex());

//BelowaretwowaysofspecifyingwhereAspose.Wordswillsaveeachpageofthedocument.
//1-Setafilenamefortheoutputpagefile:
args->set_PageFileName(outFileName);

//2-Createacustomstreamfortheoutputpagefile:
args->set_PageStream(MakeObject<System::IO::FileStream>(outFileName,System::IO::FileMode::Create));

ASSERT_FALSE(args->get_KeepPageStreamOpen());
}
};

Examples


Shows how to convert only some of the pages in a document to PDF. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

builder->Writeln(u"Page1.");
builder->InsertBreak(BreakType::PageBreak);
builder->Writeln(u"Page2.");
builder->InsertBreak(BreakType::PageBreak);
builder->Writeln(u"Page3.");

{
SharedPtr<System::IO::Stream>stream=System::IO::File::Create(ArtifactsDir+u"PdfSaveOptions.OnePage.pdf");
//Createa"PdfSaveOptions"objectthatwecanpasstothedocument's"Save"method
//tomodifyhowthatmethodconvertsthedocumentto.PDF.
autooptions=MakeObject<PdfSaveOptions>();

//Setthe"PageIndex"to"1"torenderaportionofthedocumentstartingfromthesecondpage.
options->set_PageSet(MakeObject<PageSet>(1));

//Thisdocumentwillcontainonepagestartingfrompagetwo,whichwillonlycontainthesecondpage.
doc->Save(stream,options);
}

Shows how to export Odd pages from the document. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

for(inti=0;i<5;i++)
{
builder->Writeln(String::Format(u"Page{0}({1})",i+1,(i%2==0?String(u"odd"):String(u"even"))));
if(i<4)
{
builder->InsertBreak(BreakType::PageBreak);
}
}

//Createa"PdfSaveOptions"objectthatwecanpasstothedocument's"Save"method
//tomodifyhowthatmethodconvertsthedocumentto.PDF.
autooptions=MakeObject<PdfSaveOptions>();

//BelowarethreePageSetpropertiesthatwecanusetofilteroutasetofpagesfrom
//ourdocumenttosaveinanoutputPDFdocumentbasedontheparityoftheirpagenumbers.
//1-Saveonlytheeven-numberedpages:
options->set_PageSet(PageSet::get_Even());

doc->Save(ArtifactsDir+u"PdfSaveOptions.ExportPageSet.Even.pdf",options);

//2-Saveonlytheodd-numberedpages:
options->set_PageSet(PageSet::get_Odd());

doc->Save(ArtifactsDir+u"PdfSaveOptions.ExportPageSet.Odd.pdf",options);

//3-Saveeverypage:
options->set_PageSet(PageSet::get_All());

doc->Save(ArtifactsDir+u"PdfSaveOptions.ExportPageSet.All.pdf",options);

Shows how to extract pages based on exact page indices. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

//Addfivepagestothedocument.
for(inti=1;i<6;i++)
{
builder->Write(String(u"Page")+i);
builder->InsertBreak(BreakType::PageBreak);
}

//Createan"XpsSaveOptions"object,whichwecanpasstothedocument's"Save"method
//tomodifyhowthatmethodconvertsthedocumentto.XPS.
autoxpsOptions=MakeObject<XpsSaveOptions>();

//Usethe"PageSet"propertytoselectasetofthedocument'spagestosavetooutputXPS.
//Inthiscase,wewillchoose,viaazero-basedindex,onlythreepages:page1,page2,andpage4.
xpsOptions->set_PageSet(MakeObject<PageSet>(MakeArray<int>({0,1,3})));

doc->Save(ArtifactsDir+u"XpsSaveOptions.ExportExactPages.xps",xpsOptions);

