Examples


Shows how to place CSS into a separate file and add a prefix to all of its CSS class names. autodoc=MakeObject<Document>(MyDir+u"Bookmarks.docx");

autohtmlFixedSaveOptions=MakeObject<HtmlFixedSaveOptions>();
htmlFixedSaveOptions->set_CssClassNamesPrefix(u"myprefix");
htmlFixedSaveOptions->set_SaveFontFaceCssSeparately(true);

doc->Save(ArtifactsDir+u"HtmlFixedSaveOptions.AddCssClassNamesPrefix.html",htmlFixedSaveOptions);

StringoutDocContents=System::IO::File::ReadAllText(ArtifactsDir+u"HtmlFixedSaveOptions.AddCssClassNamesPrefix.html");

ASSERT_TRUE(System::Text::RegularExpressions::Regex::Match(
outDocContents,
String(u"<divclass=\"myprefixdivmyprefixpage\"style=\"width:595[.]3pt;height:841[.]9pt;\">")+
u"<divclass=\"myprefixdiv\"style=\"left:85[.]05pt;top:36pt;clip:rect[(]0pt,510[.]25pt,74[.]95pt,-85.05pt[)];\">"+
u"<spanclass=\"myprefixspanmyprefixtext001\"style=\"font-size:11pt;left:294[.]73pt;top:0[.]36pt;line-height:12[.]29pt;\">")
->get_Success());

outDocContents=System::IO::File::ReadAllText(ArtifactsDir+u"HtmlFixedSaveOptions.AddCssClassNamesPrefix/styles.css");

ASSERT_TRUE(System::Text::RegularExpressions::Regex::Match(outDocContents,
String(u".myprefixdiv{position:absolute;}")+
u".myprefixspan{position:absolute;white-space:pre;color:#000000;font-size:12pt;}")
->get_Success());

