Shows how to set which encoding to use while exporting a document to HTML. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

builder->Writeln(u"HelloWorld!");

//ThedefaultencodingisUTF-8.Ifwewanttorepresentourdocumentusingadifferentencoding,
//wecanuseaSaveOptionsobjecttosetaspecificencoding.
autohtmlFixedSaveOptions=MakeObject<HtmlFixedSaveOptions>();
htmlFixedSaveOptions->set_Encoding(System::Text::Encoding::GetEncoding(u"ASCII"));

ASSERT_EQ(u"US-ASCII",htmlFixedSaveOptions->get_Encoding()->get_EncodingName());

doc->Save(ArtifactsDir+u"HtmlFixedSaveOptions.UseEncoding.html",htmlFixedSaveOptions);

Examples


Shows how to determine where to store CSS stylesheets when exporting a document to Html. autodoc=MakeObject<Document>(MyDir+u"Rendering.docx");

//Whenweexportadocumenttohtml,Aspose.WordswillalsocreateaCSSstylesheettoformatthedocumentwith.
//Settingthe"ExportEmbeddedCss"flagto"true"savetheCSSstylesheettoa.cssfile,
//andlinktothefilefromthehtmldocumentusinga<link>element.
//Settingtheflagto"false"willembedtheCSSstylesheetwithintheHtmldocument,
//whichwillcreateonlyonefileinsteadoftwo.
autohtmlFixedSaveOptions=MakeObject<HtmlFixedSaveOptions>();
htmlFixedSaveOptions->set_ExportEmbeddedCss(exportEmbeddedCss);

doc->Save(ArtifactsDir+u"HtmlFixedSaveOptions.ExportEmbeddedCss.html",htmlFixedSaveOptions);

StringoutDocContents=System::IO::File::ReadAllText(ArtifactsDir+u"HtmlFixedSaveOptions.ExportEmbeddedCss.html");

if(exportEmbeddedCss)
{
ASSERT_TRUE(System::Text::RegularExpressions::Regex::Match(outDocContents,u"<styletype=\"text/css\">")->get_Success());
ASSERT_FALSE(System::IO::File::Exists(ArtifactsDir+u"HtmlFixedSaveOptions.ExportEmbeddedCss/styles.css"));
}
else
{
ASSERT_TRUE(System::Text::RegularExpressions::Regex::Match(
outDocContents,
u"<linkrel=\"stylesheet\"type=\"text/css\"href=\"HtmlFixedSaveOptions[.]ExportEmbeddedCss/styles[.]css\"media=\"all\"/>")
->get_Success());
ASSERT_TRUE(System::IO::File::Exists(ArtifactsDir+u"HtmlFixedSaveOptions.ExportEmbeddedCss/styles.css"));
}

Examples


Shows how to determine where to store embedded fonts when exporting a document to Html. autodoc=MakeObject<Document>(MyDir+u"Embeddedfont.docx");

//Whenweexportadocumentwithembeddedfontsto.html,
//Aspose.Wordscanplacethefontsintwopossiblelocations.
//Settingthe"ExportEmbeddedFonts"flagto"true"willstoretherawdataforembeddedfontswithintheCSSstylesheet,
//inthe"url"propertyofthe"@font-face"rule.ThismaycreateahugeCSSstylesheetfile
//andreducethenumberofexternalfilesthatthisHTMLconversionwillcreate.
//Settingthisflagto"false"willcreateafileforeachfont.
//TheCSSstylesheetwilllinktoeachfontfileusingthe"url"propertyofthe"@font-face"rule.
autohtmlFixedSaveOptions=MakeObject<HtmlFixedSaveOptions>();
htmlFixedSaveOptions->set_ExportEmbeddedFonts(exportEmbeddedFonts);

doc->Save(ArtifactsDir+u"HtmlFixedSaveOptions.ExportEmbeddedFonts.html",htmlFixedSaveOptions);

StringoutDocContents=System::IO::File::ReadAllText(ArtifactsDir+u"HtmlFixedSaveOptions.ExportEmbeddedFonts/styles.css");

if(exportEmbeddedFonts)
{
ASSERT_TRUE(System::Text::RegularExpressions::Regex::Match(
outDocContents,
u"@font-face{font-family:'Arial';font-style:normal;font-weight:normal;src:local[(]'☺'[)],url[(].+[)]format[(]'woff'[)];}")
->get_Success());
ASSERT_EQ(0,System::IO::Directory::GetFiles(ArtifactsDir+u"HtmlFixedSaveOptions.ExportEmbeddedFonts")
->LINQ_Count([](Stringf){returnf.EndsWith(u".woff");}));
}
else
{
ASSERT_TRUE(
System::Text::RegularExpressions::Regex::Match(outDocContents,u"@font-face{font-family:'Arial';font-style:normal;font-weight:normal;"
u"src:local[(]'☺'[)],url[(]'font001[.]woff'[)]format[(]'woff'[)];}")
->get_Success());
ASSERT_EQ(2,System::IO::Directory::GetFiles(ArtifactsDir+u"HtmlFixedSaveOptions.ExportEmbeddedFonts")
->LINQ_Count([](Stringf){returnf.EndsWith(u".woff");}));
}

