Shows how to determine where to store images when exporting a document to Html. autodoc=MakeObject<Document>(MyDir+u"Images.docx");

//Whenweexportadocumentwithembeddedimagesto.html,
//Aspose.Wordscanplacetheimagesintwopossiblelocations.
//Settingthe"ExportEmbeddedImages"flagto"true"willstoretherawdata
//forallimageswithintheoutputHTMLdocument,inthe"src"attributeof<image>tags.
//Settingthisflagto"false"willcreateanimagefileinthelocalfilesystemforeveryimage,
//andstoreallthesefilesinaseparatefolder.
autohtmlFixedSaveOptions=MakeObject<HtmlFixedSaveOptions>();
htmlFixedSaveOptions->set_ExportEmbeddedImages(exportImages);

doc->Save(ArtifactsDir+u"HtmlFixedSaveOptions.ExportEmbeddedImages.html",htmlFixedSaveOptions);

StringoutDocContents=System::IO::File::ReadAllText(ArtifactsDir+u"HtmlFixedSaveOptions.ExportEmbeddedImages.html");

if(exportImages)
{
ASSERT_FALSE(System::IO::File::Exists(ArtifactsDir+u"HtmlFixedSaveOptions.ExportEmbeddedImages/image001.jpeg"));
ASSERT_TRUE(System::Text::RegularExpressions::Regex::Match(
outDocContents,u"<imgclass=\"awimg\"style=\"left:0pt;top:0pt;width:493.1pt;height:300.55pt;\"src=\".+\"/>")
->get_Success());
}
else
{
ASSERT_TRUE(System::IO::File::Exists(ArtifactsDir+u"HtmlFixedSaveOptions.ExportEmbeddedImages/image001.jpeg"));
ASSERT_TRUE(System::Text::RegularExpressions::Regex::Match(
outDocContents,String(u"<imgclass=\"awimg\"style=\"left:0pt;top:0pt;width:493.1pt;height:300.55pt;\"")+
u"src=\"HtmlFixedSaveOptions[.]ExportEmbeddedImages/image001[.]jpeg\"/>")
->get_Success());
}

