Shows how to export form fields to Html. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

builder->InsertCheckBox(u"CheckBox",false,15);

//Whenweexportadocumentwithformfieldsto.html,
//therearetwowaysinwhichAspose.Wordscanexportformfields.
//Settingthe"ExportFormFields"flagto"true"willexportthemasinteractiveobjects.
//Settingthisflagto"false"willdisplayformfieldsasplaintext.
//Thiswillfreezethemattheircurrentvalue,andpreventthereaderofourHTMLdocument
//frombeingabletointeractwiththem.
autohtmlFixedSaveOptions=MakeObject<HtmlFixedSaveOptions>();
htmlFixedSaveOptions->set_ExportFormFields(exportFormFields);

doc->Save(ArtifactsDir+u"HtmlFixedSaveOptions.ExportFormFields.html",htmlFixedSaveOptions);

StringoutDocContents=System::IO::File::ReadAllText(ArtifactsDir+u"HtmlFixedSaveOptions.ExportFormFields.html");

if(exportFormFields)
{
ASSERT_TRUE(System::Text::RegularExpressions::Regex::Match(
outDocContents,String(u"<aname=\"CheckBox\"style=\"left:0pt;top:0pt;\"></a>")+
u"<inputstyle=\"position:absolute;left:0pt;top:0pt;\"type=\"checkbox\"name=\"CheckBox\"/>")
->get_Success());
}
else
{
ASSERT_TRUE(
System::Text::RegularExpressions::Regex::Match(
outDocContents,String(u"<aname=\"CheckBox\"style=\"left:0pt;top:0pt;\"></a>")+
u"<divclass=\"awdiv\"style=\"left:0.8pt;top:0.8pt;width:14.25pt;height:14.25pt;border:solid0.75pt#000000;\"")
->get_Success());
}

