Shows how to set the horizontal alignment of pages when saving a document to HTML. autodoc=MakeObject<Document>(MyDir+u"Rendering.docx");

autohtmlFixedSaveOptions=MakeObject<HtmlFixedSaveOptions>();
htmlFixedSaveOptions->set_PageHorizontalAlignment(pageHorizontalAlignment);

doc->Save(ArtifactsDir+u"HtmlFixedSaveOptions.HorizontalAlignment.html",htmlFixedSaveOptions);

StringoutDocContents=System::IO::File::ReadAllText(ArtifactsDir+u"HtmlFixedSaveOptions.HorizontalAlignment/styles.css");

switch(pageHorizontalAlignment)
{
caseHtmlFixedPageHorizontalAlignment::Center:
ASSERT_TRUE(System::Text::RegularExpressions::Regex::Match(
outDocContents,u"[.]awpage{position:relative;border:solid1ptblack;margin:10ptauto10ptauto;overflow:hidden;}")
->get_Success());
break;

caseHtmlFixedPageHorizontalAlignment::Left:
ASSERT_TRUE(System::Text::RegularExpressions::Regex::Match(
outDocContents,u"[.]awpage{position:relative;border:solid1ptblack;margin:10ptauto10pt10pt;overflow:hidden;}")
->get_Success());
break;

caseHtmlFixedPageHorizontalAlignment::Right:
ASSERT_TRUE(System::Text::RegularExpressions::Regex::Match(
outDocContents,u"[.]awpage{position:relative;border:solid1ptblack;margin:10pt10pt10ptauto;overflow:hidden;}")
->get_Success());
break;
}

