doc->Save(ArtifactsDir+u"HtmlSaveOptions.EpubHeadings.epub",options);

Examples


Shows how to enable content IDs for output MHTML documents. autodoc=MakeObject<Document>(MyDir+u"Rendering.docx");

//Settingthisflagwillreplace"Content-Location"tags
//with"Content-ID"tagsforeachresourcefromtheinputdocument.
autooptions=MakeObject<HtmlSaveOptions>(SaveFormat::Mhtml);
options->set_ExportCidUrlsForMhtmlResources(exportCidUrlsForMhtmlResources);
options->set_CssStyleSheetType(CssStyleSheetType::External);
options->set_ExportFontResources(true);
options->set_PrettyFormat(true);

doc->Save(ArtifactsDir+u"HtmlSaveOptions.ContentIdUrls.mht",options);

StringoutDocContents=System::IO::File::ReadAllText(ArtifactsDir+u"HtmlSaveOptions.ContentIdUrls.mht");

if(exportCidUrlsForMhtmlResources)
{
ASSERT_TRUE(outDocContents.Contains(u"Content-ID:<document.html>"));
ASSERT_TRUE(outDocContents.Contains(u"<linkhref=3D\"cid:styles.css\"type=3D\"text/css\"rel=3D\"stylesheet\"/>"));
ASSERT_TRUE(outDocContents.Contains(u"@font-face{font-family:'ArialBlack';src:url('cid:ariblk.ttf')}"));
ASSERT_TRUE(outDocContents.Contains(u"<imgsrc=3D\"cid:image.003.jpeg\"width=3D\"350\"height=3D\"180\"alt=3D\"\"/>"));
}
else
{
ASSERT_TRUE(outDocContents.Contains(u"Content-Location:document.html"));
ASSERT_TRUE(outDocContents.Contains(u"<linkhref=3D\"styles.css\"type=3D\"text/css\"rel=3D\"stylesheet\"/>"));
ASSERT_TRUE(outDocContents.Contains(u"@font-face{font-family:'ArialBlack';src:url('ariblk.ttf')}"));
ASSERT_TRUE(outDocContents.Contains(u"<imgsrc=3D\"image.003.jpeg\"width=3D\"350\"height=3D\"180\"alt=3D\"\"/>"));
}

