Shows how to get drop-down combo box form fields to blend in with paragraph text when saving to html. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

//Useadocumentbuildertoinsertacomboboxwiththevalue"Two"selected.
builder->InsertComboBox(u"MyComboBox",MakeArray<String>({u"One",u"Two",u"Three"}),1);

//The"ExportDropDownFormFieldAsText"flagofthisSaveOptionsobjectallowsusto
//controlhowsavingthedocumenttoHTMLtreatsdrop-downcomboboxes.
//Settingitto"true"willconverteachcomboboxintosimpletext
//thatdisplaysthecombobox'scurrentlyselectedvalue,effectivelyfreezingit.
//Settingitto"false"willpreservethefunctionalityofthecomboboxusing<select>and<option>tags.
autooptions=MakeObject<HtmlSaveOptions>();
options->set_ExportDropDownFormFieldAsText(exportDropDownFormFieldAsText);

doc->Save(ArtifactsDir+u"HtmlSaveOptions.DropDownFormField.html",options);

StringoutDocContents=System::IO::File::ReadAllText(ArtifactsDir+u"HtmlSaveOptions.DropDownFormField.html");

if(exportDropDownFormFieldAsText)
{
ASSERT_TRUE(outDocContents.Contains(u"<span>Two</span>"));
}
else
{
ASSERT_TRUE(outDocContents.Contains(String(u"<selectname=\"MyComboBox\">")+u"<option>One</option>"+
u"<optionselected=\"selected\">Two</option>"+u"<option>Three</option>"+u"</select>"));
}

