Examples


Shows how to define custom logic for exporting fonts when saving to HTML. voidSaveExportedFonts()
{
autodoc=MakeObject<Document>(MyDir+u"Rendering.docx");

//ConfigureaSaveOptionsobjecttoexportfontstoseparatefiles.
//Setacallbackthatwillhandlefontsavinginacustommanner.
autooptions=MakeObject<HtmlSaveOptions>();
options->set_ExportFontResources(true);
options->set_FontSavingCallback(MakeObject<ExHtmlSaveOptions::HandleFontSaving>());

//Thecallbackwillexport.ttffilesandsavethemalongsidetheoutputdocument.
doc->Save(ArtifactsDir+u"HtmlSaveOptions.SaveExportedFonts.html",options);

std::function<bool(Strings)>endsWithTtf=[](Strings)
{
returns.EndsWith(u".ttf");
};

for(StringfontFilename:System::Array<String>::FindAll(System::IO::Directory::GetFiles(ArtifactsDir),endsWithTtf))
{
std::cout<<fontFilename<<std::endl;
}

}

classHandleFontSaving:publicIFontSavingCallback
{
private:
voidFontSaving(SharedPtr<FontSavingArgs>args)override
{
std::cout<<"Font:\t"<<args->get_FontFamilyName();
if(args->get_Bold())
{
std::cout<<",bold";
}
if(args->get_Italic())
{
std::cout<<",italic";
}
std::cout<<"\nSource:\t"<<args->get_OriginalFileName()<<","<<args->get_OriginalFileSize()<<"bytes\n"<<std::endl;

//Wecanalsoaccessthesourcedocumentfromhere.
ASSERT_TRUE(args->get_Document()->get_OriginalFileName().EndsWith(u"Rendering.docx"));

ASSERT_TRUE(args->get_IsExportNeeded());
ASSERT_TRUE(args->get_IsSubsettingNeeded());

//Therearetwowaysofsavinganexportedfont.
//1-Saveittoalocalfilesystemlocation:
args->set_FontFileName(args->get_OriginalFileName().Split(MakeArray<char16_t>({System::IO::Path::DirectorySeparatorChar}))->LINQ_Last());

//2-Saveittoastream:
args->set_FontStream(MakeObject<System::IO::FileStream>(
ArtifactsDir+args->get_OriginalFileName().Split(MakeArray<char16_t>({System::IO::Path::DirectorySeparatorChar}))->LINQ_Last(),
System::IO::FileMode::Create));
ASSERT_FALSE(args->get_KeepFontStreamOpen());
}
};

