Examples


Shows how to show out-of-bounds objects in output HTML documents. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

//Useabuildertoinsertashapewithnowrapping.
SharedPtr<Shape>shape=builder->InsertShape(ShapeType::Cube,200,200);

shape->set_RelativeHorizontalPosition(RelativeHorizontalPosition::Page);
shape->set_RelativeVerticalPosition(RelativeVerticalPosition::Page);
shape->set_WrapType(WrapType::None);

//Negativeshapepositionvaluesmayplacetheshapeoutsideofpageboundaries.
//IfweexportthistoHTML,theshapewillappeartruncated.
shape->set_Left(-150);

//WhensavingthedocumenttoHTML,wecanpassaSaveOptionsobject
//todecidewhethertoadjustthepagetodisplayout-of-boundsobjectsfully.
//Ifwesetthe"ExportPageMargins"flagto"true",theshapewillbefullyvisibleintheoutputHTML.
//Ifwesetthe"ExportPageMargins"flagto"false",
//ourdocumentwilldisplaytheshapetruncatedaswewouldseeitinMicrosoftWord.
autooptions=MakeObject<HtmlSaveOptions>();
options->set_ExportPageMargins(exportPageMargins);

doc->Save(ArtifactsDir+u"HtmlSaveOptions.ExportPageMargins.html",options);

StringoutDocContents=System::IO::File::ReadAllText(ArtifactsDir+u"HtmlSaveOptions.ExportPageMargins.html");

if(exportPageMargins)
{
ASSERT_TRUE(outDocContents.Contains(u"<styletype=\"text/css\">div.Section1{margin:70.85pt}</style>"));
ASSERT_TRUE(outDocContents.Contains(u"<divclass=\"Section1\"><pstyle=\"margin-top:0pt;margin-left:151pt;margin-bottom:0pt\">"));
}
else
{
ASSERT_FALSE(outDocContents.Contains(u"styletype=\"text/css\">"));
ASSERT_TRUE(outDocContents.Contains(u"<div><pstyle=\"margin-top:0pt;margin-left:221.85pt;margin-bottom:0pt\">"));
}

Examples


Shows how decide whether to preserve section structure/page setup information when saving to HTML. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

builder->Write(u"Section1");
builder->InsertBreak(BreakType::SectionBreakNewPage);
builder->Write(u"Section2");

SharedPtr<PageSetup>pageSetup=doc->get_Sections()->idx_get(0)->get_PageSetup();
pageSetup->set_TopMargin(36.0);
pageSetup->set_BottomMargin(36.0);
pageSetup->set_PaperSize(PaperSize::A5);

//WhensavingthedocumenttoHTML,wecanpassaSaveOptionsobject
//todecidewhethertopreserveordiscardpagesetupsettings.
//Ifwesetthe"ExportPageSetup"flagto"true",theoutputHTMLdocumentwillcontainourpagesetupconfiguration.
//Ifwesetthe"ExportPageSetup"flagto"false",thesaveoperationwilldiscardourpagesetupsettings
//forthefirstsection,andbothsectionswilllookidentical.
autooptions=MakeObject<HtmlSaveOptions>();
options->set_ExportPageSetup(exportPageSetup);

doc->Save(ArtifactsDir+u"HtmlSaveOptions.ExportPageSetup.html",options);

StringoutDocContents=System::IO::File::ReadAllText(ArtifactsDir+u"HtmlSaveOptions.ExportPageSetup.html");

if(exportPageSetup)
{
ASSERT_TRUE(outDocContents.Contains(String(u"<styletype=\"text/css\">")+u"@pageSection1{size:419.55pt595.3pt;margin:36pt70.85pt}"+
u"@pageSection2{size:612pt792pt;margin:70.85pt}"+
u"div.Section1{page:Section1}div.Section2{page:Section2}"+u"</style>"));

ASSERT_TRUE(outDocContents.Contains(String(u"<divclass=\"Section1\">")+u"<pstyle=\"margin-top:0pt;margin-bottom:0pt\">"+
u"<span>Section1</span>"+u"</p>"+u"</div>"));
}
else
{
ASSERT_FALSE(outDocContents.Contains(u"styletype=\"text/css\">"));

ASSERT_TRUE(outDocContents.Contains(String(u"<div>")+u"<pstyle=\"margin-top:0pt;margin-bottom:0pt\">"+u"<span>Section1</span>"+u"</p>"+
u"</div>"));
}

Examples


Shows how to use relative font sizes when saving to .html. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

builder->Writeln(u"Defaultfontsize,");
builder->get_Font()->set_Size(24);
builder->Writeln(u"2xdefaultfontsize,");
builder->get_Font()->set_Size(96);
builder->Write(u"8xdefaultfontsize");

//WhenwesavethedocumenttoHTML,wecanpassaSaveOptionsobject
//todeterminewhethertouserelativeorabsolutefontsizes.
//Setthe"ExportRelativeFontSize"flagto"true"todeclarefontsizes
//usingthe"em"measurementunit,whichisafactorthatmultipliesthecurrentfontsize.
//Setthe"ExportRelativeFontSize"flagto"false"todeclarefontsizes
//usingthe"pt"measurementunit,whichisthefont'sabsolutesizeinpoints.
autooptions=MakeObject<HtmlSaveOptions>();
options->set_ExportRelativeFontSize(exportRelativeFontSize);

doc->Save(ArtifactsDir+u"HtmlSaveOptions.RelativeFontSize.html",options);

StringoutDocContents=System::IO::File::ReadAllText(ArtifactsDir+u"HtmlSaveOptions.RelativeFontSize.html");

if(exportRelativeFontSize)
{
ASSERT_TRUE(outDocContents.Contains(String(u"<bodystyle=\"font-family:'TimesNewRoman'\">")+u"<div>"+
u"<pstyle=\"margin-top:0pt;margin-bottom:0pt\">"+u"<span>Defaultfontsize,</span>"+u"</p>"+
u"<pstyle=\"margin-top:0pt;margin-bottom:0pt;font-size:2em\">"+u"<span>2xdefaultfontsize,</span>"+
u"</p>"+u"<pstyle=\"margin-top:0pt;margin-bottom:0pt;font-size:8em\">"+
u"<span>8xdefaultfontsize</span>"+u"</p>"+u"</div>"+u"</body>"));
}
else
{
ASSERT_TRUE(outDocContents.Contains(String(u"<bodystyle=\"font-family:'TimesNewRoman';font-size:12pt\">")+u"<div>"+
u"<pstyle=\"margin-top:0pt;margin-bottom:0pt\">"+u"<span>Defaultfontsize,</span>"+u"</p>"+
u"<pstyle=\"margin-top:0pt;margin-bottom:0pt;font-size:24pt\">"+u"<span>2xdefaultfontsize,</span>"+
u"</p>"+u"<pstyle=\"margin-top:0pt;margin-bottom:0pt;font-size:96pt\">"+
u"<span>8xdefaultfontsize</span>"+u"</p>"+u"</div>"+u"</body>"));
}

Examples


Shows how to preserve hidden elements when converting to .html. autodoc=MakeObject<Document>(MyDir+u"Rendering.docx");

//Whenconvertingadocumentto.html,someelementssuchashiddenbookmarks,originalshapepositions,
//orfootnoteswillbeeitherremovedorconvertedtoplaintextandeffectivelybelost.
//SavingwithaHtmlSaveOptionsobjectwithExportRoundtripInformationsettotruewillpreservetheseelements.

//WhenwesavethedocumenttoHTML,wecanpassaSaveOptionsobjecttodetermine
//howthesavingoperationwillexportdocumentelementsthatHTMLdoesnotsupportoruse,
//suchashiddenbookmarksandoriginalshapepositions.
//Ifwesetthe"ExportRoundtripInformation"flagto"true",thesaveoperationwillpreservetheseelements.
//Ifwesetthe"ExportRoundTripInformation"flagto"false",thesaveoperationwilldiscardtheseelements.
//WewillwanttopreservesuchelementsifweintendtoloadthesavedHTMLusingAspose.Words,
//astheycouldbeofuseonceagain.
autooptions=MakeObject<HtmlSaveOptions>();
options->set_ExportRoundtripInformation(exportRoundtripInformation);

doc->Save(ArtifactsDir+u"HtmlSaveOptions.RoundTripInformation.html",options);

StringoutDocContents=System::IO::File::ReadAllText(ArtifactsDir+u"HtmlSaveOptions.RoundTripInformation.html");
doc=MakeObject<Document>(ArtifactsDir+u"HtmlSaveOptions.RoundTripInformation.html");

if(exportRoundtripInformation)
{
ASSERT_TRUE(outDocContents.Contains(u"<divstyle=\"-aw-headerfooter-type:header-primary;clear:both\">"));
ASSERT_TRUE(outDocContents.Contains(u"<spanstyle=\"-aw-import:ignore\">&#xa0;</span>"));

ASSERT_TRUE(outDocContents.Contains(String(u"tdcolspan=\"2\"style=\"width:210.6pt;border-style:solid;border-width:0.75pt6pt0.75pt0.75pt;")+
u"padding-right:2.4pt;padding-left:5.03pt;vertical-align:top;"+
u"-aw-border-bottom:0.5ptsingle;-aw-border-left:0.5ptsingle;-aw-border-top:0.5ptsingle\">"));

ASSERT_TRUE(outDocContents.Contains(
u"<listyle=\"margin-left:30.2pt;padding-left:5.8pt;-aw-font-family:'CourierNew';-aw-font-weight:normal;-aw-number-format:'o'\">"));

ASSERT_TRUE(
outDocContents.Contains(String(u"<imgsrc=\"HtmlSaveOptions.RoundTripInformation.003.jpeg\"width=\"350\"height=\"180\"alt=\"\"")+
u"style=\"-aw-left-pos:0pt;-aw-rel-hpos:column;-aw-rel-vpos:paragraph;-aw-top-pos:0pt;-aw-wrap-type:inline\"/>"));

ASSERT_TRUE(outDocContents.Contains(String(u"<span>Pagenumber</span>")+u"<spanstyle=\"-aw-field-start:true\"></span>"+
u"<spanstyle=\"-aw-field-code:'PAGE\\\\*MERGEFORMAT'\"></span>"+
u"<spanstyle=\"-aw-field-separator:true\"></span>"+u"<span>1</span>"+
u"<spanstyle=\"-aw-field-end:true\"></span>"));

ASSERT_EQ(1,doc->get_Range()->get_Fields()->LINQ_Count([](SharedPtr<Field>f){returnf->get_Type()==FieldType::FieldPage;}));
}
else
{
ASSERT_TRUE(outDocContents.Contains(u"<divstyle=\"clear:both\">"));
ASSERT_TRUE(outDocContents.Contains(u"<span>&#xa0;</span>"));

ASSERT_TRUE(
outDocContents.Contains(String(u"<tdcolspan=\"2\"style=\"width:210.6pt;border-style:solid;border-width:0.75pt6pt0.75pt0.75pt;")+
u"padding-right:2.4pt;padding-left:5.03pt;vertical-align:top\">"));

ASSERT_TRUE(outDocContents.Contains(u"<listyle=\"margin-left:30.2pt;padding-left:5.8pt\">"));

ASSERT_TRUE(outDocContents.Contains(u"<imgsrc=\"HtmlSaveOptions.RoundTripInformation.003.jpeg\"width=\"350\"height=\"180\"alt=\"\"/>"));

ASSERT_TRUE(outDocContents.Contains(u"<span>Pagenumber1</span>"));

ASSERT_EQ(0,doc->get_Range()->get_Fields()->LINQ_Count([](SharedPtr<Field>f){returnf->get_Type()==FieldType::FieldPage;}));
}

Examples


Shows how to export text boxes as scalable vector graphics. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

SharedPtr<Shape>textBox=builder->InsertShape(ShapeType::TextBox,100.0,60.0);
builder->MoveTo(textBox->get_FirstParagraph());
builder->Write(u"Mytextbox");

//WhenwesavethedocumenttoHTML,wecanpassaSaveOptionsobject
//todeterminehowthesavingoperationwillexporttextboxshapes.
//Ifwesetthe"ExportShapesAsSvg"flagto"true",
//thesaveoperationwillconvertshapeswithtextintoSVGobjects.
//Ifwesetthe"ExportShapesAsSvg"flagto"false",
//thesaveoperationwillconvertshapeswithtextintoimages.
autooptions=MakeObject<HtmlSaveOptions>();
options->set_ExportShapesAsSvg(exportShapesAsSvg);

doc->Save(ArtifactsDir+u"HtmlSaveOptions.ExportTextBox.html",options);

StringoutDocContents=System::IO::File::ReadAllText(ArtifactsDir+u"HtmlSaveOptions.ExportTextBox.html");

if(exportShapesAsSvg)
{
ASSERT_TRUE(outDocContents.Contains(
String(u"<spanstyle=\"-aw-left-pos:0pt;-aw-rel-hpos:column;-aw-rel-vpos:paragraph;-aw-top-pos:0pt;-aw-wrap-type:inline\">")+
u"<svgxmlns=\"http://www.w3.org/2000/svg\"xmlns:xlink=\"http://www.w3.org/1999/xlink\"version=\"1.1\"width=\"133\"height=\"80\">"));
}
else
{
ASSERT_TRUE(outDocContents.Contains(
String(u"<pstyle=\"margin-top:0pt;margin-bottom:0pt\">")+
u"<imgsrc=\"HtmlSaveOptions.ExportTextBox.001.png\"width=\"136\"height=\"83\"alt=\"\""+
u"style=\"-aw-left-pos:0pt;-aw-rel-hpos:column;-aw-rel-vpos:paragraph;-aw-top-pos:0pt;-aw-wrap-type:inline\"/>"+u"</p>"));
}

