Examples


Shows how to display page numbers when saving a document with a table of contents to .html. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

//Insertatableofcontents,andthenpopulatethedocumentwithparagraphsformattedusinga"Heading"
//stylethatthetableofcontentswillpickupasentries.Eachentrywilldisplaytheheadingparagraphontheleft,
//andthepagenumberthatcontainstheheadingontheright.
autofieldToc=System::DynamicCast<FieldToc>(builder->InsertField(FieldType::FieldTOC,true));

builder->get_ParagraphFormat()->set_Style(builder->get_Document()->get_Styles()->idx_get(u"Heading1"));
builder->InsertBreak(BreakType::PageBreak);
builder->Writeln(u"Entry1");
builder->Writeln(u"Entry2");
builder->InsertBreak(BreakType::PageBreak);
builder->Writeln(u"Entry3");
builder->InsertBreak(BreakType::PageBreak);
builder->Writeln(u"Entry4");
fieldToc->UpdatePageNumbers();
doc->UpdateFields();

//HTMLdocumentsdonothavepages.IfwesavethisdocumenttoHTML,
//thepagenumbersthatourTOCdisplayswillhavenomeaning.
//WhenwesavethedocumenttoHTML,wecanpassaSaveOptionsobjecttoomitthesepagenumbersfromtheTOC.
//Ifwesetthe"ExportTocPageNumbers"flagto"true",
//eachTOCentrywilldisplaytheheading,separator,andpagenumber,preservingitsappearanceinMicrosoftWord.
//Ifwesetthe"ExportTocPageNumbers"flagto"false",
//thesaveoperationwillomitboththeseparatorandpagenumberandleavetheheadingforeachentryintact.
autooptions=MakeObject<HtmlSaveOptions>();
options->set_ExportTocPageNumbers(exportTocPageNumbers);

doc->Save(ArtifactsDir+u"HtmlSaveOptions.ExportTocPageNumbers.html",options);

StringoutDocContents=System::IO::File::ReadAllText(ArtifactsDir+u"HtmlSaveOptions.ExportTocPageNumbers.html");

if(exportTocPageNumbers)
{
ASSERT_TRUE(outDocContents.Contains(
String(u"<pstyle=\"margin-top:0pt;margin-bottom:0pt\">")+u"<span>Entry1</span>"+
u"<spanstyle=\"width:428.14pt;font-family:'LucidaConsole';font-size:10pt;display:inline-block;-aw-font-family:'TimesNewRoman';"+
u"-aw-tabstop-align:right;-aw-tabstop-leader:dots;"
u"-aw-tabstop-pos:469.8pt\">.......................................................................</span>"+
u"<span>2</span>"+u"</p>"));
}
else
{
ASSERT_TRUE(outDocContents.Contains(String(u"<pstyle=\"margin-top:0pt;margin-bottom:0pt\">")+u"<span>Entry1</span>"+u"</p>"));
}

