autobuilder=MakeObject<DocumentBuilder>(doc);

builder->Writeln(u"Helloworld!");

autooptions=MakeObject<HtmlSaveOptions>(SaveFormat::Html);
options->set_HtmlVersion(HtmlVersion::Xhtml);
options->set_ExportXhtmlTransitional(showDoctypeDeclaration);
options->set_PrettyFormat(true);

doc->Save(ArtifactsDir+u"HtmlSaveOptions.ExportXhtmlTransitional.html",options);

//OurdocumentwillonlycontainaDOCTYPEdeclarationheadingifwehavesetthe"ExportXhtmlTransitional"flagto"true".
