{
ASSERT_TRUE(outDocContents.Contains(String(u"<?xmlversion=\"1.0\"encoding=\"utf-8\"standalone=\"no\"?>\r\n")+
u"<!DOCTYPEhtmlPUBLIC\"-//W3C//DTDXHTML1.0Transitional//EN\""
u"\"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\r\n"+
u"<htmlxmlns=\"http://www.w3.org/1999/xhtml\">"));
}
else
{
ASSERT_TRUE(outDocContents.Contains(u"<html>"));
}

Aspose::Words::Saving::HtmlSaveOptions::get_ExportFontResources

Examples


Shows how to work with font subsetting. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

builder->get_Font()->set_Name(u"Arial");
builder->Writeln(u"Helloworld!");
builder->get_Font()->set_Name(u"TimesNewRoman");
builder->Writeln(u"Helloworld!");
builder->get_Font()->set_Name(u"CourierNew");
builder->Writeln(u"Helloworld!");

//WhenwesavethedocumenttoHTML,wecanpassaSaveOptionsobjectconfigurefontsubsetting.
//Supposewesetthe"ExportFontResources"flagto"true"andalsonameafolderinthe"FontsFolder"property.
//Inthatcase,thesavingoperationwillcreatethatfolderandplacea.ttffileinside
//thatfolderforeachfontthatourdocumentuses.
//Each.ttffilewillcontainthatfont'sentireglyphset,
//whichmaypotentiallyresultinaverylargefilethataccompaniesthedocument.
//Whenweapplysubsettingtoafont,itsexportedrawdatawillonlycontaintheglyphsthatthedocumentis
//usinginsteadoftheentireglyphset.Ifthetextinourdocumentonlyusesasmallfractionofafont's
//glyphset,thensubsettingwillsignificantlyreduceouroutputdocuments'size.
//Wecanusethe"FontResourcesSubsettingSizeThreshold"propertytodefinea.ttffilesize,inbytes.
//Ifanexportedfontcreatesasizebiggerfilethanthat,thenthesaveoperationwillapplysubsettingtothatfont.
//Settingathresholdof0appliessubsettingtoallfonts,
//andsettingitto"int.MaxValue"effectivelydisablessubsetting.
StringfontsFolder=ArtifactsDir+u"HtmlSaveOptions.FontSubsetting.Fonts";

autooptions=MakeObject<HtmlSaveOptions>();
options->set_ExportFontResources(true);
options->set_FontsFolder(fontsFolder);
options->set_FontResourcesSubsettingSizeThreshold(fontResourcesSubsettingSizeThreshold);

doc->Save(ArtifactsDir+u"HtmlSaveOptions.FontSubsetting.html",options);

ArrayPtr<String>fontFileNames=System::IO::Directory::GetFiles(fontsFolder)->LINQ_Where([](Strings){returns.EndsWith(u".ttf");})->LINQ_ToArray();

ASSERT_EQ(3,fontFileNames->get_Length());

for(Stringfilename:fontFileNames)
{
//Bydefault,the.ttffilesforeachofourthreefontswillbeover700MB.
//Subsettingwillreducethemalltounder30MB.
autofontFileInfo=MakeObject<System::IO::FileInfo>(filename);

ASSERT_TRUE(fontFileInfo->get_Length()>700000||fontFileInfo->get_Length()<30000);
ASSERT_TRUE(System::Math::Max(fontResourcesSubsettingSizeThreshold,30000)>MakeObject<System::IO::FileInfo>(filename)->get_Length());
}

Examples


Shows how to define custom logic for exporting fonts when saving to HTML. voidSaveExportedFonts()
{
autodoc=MakeObject<Document>(MyDir+u"Rendering.docx");

//ConfigureaSaveOptionsobjecttoexportfontstoseparatefiles.
//Setacallbackthatwillhandlefontsavinginacustommanner.
autooptions=MakeObject<HtmlSaveOptions>();
options->set_ExportFontResources(true);
options->set_FontSavingCallback(MakeObject<ExHtmlSaveOptions::HandleFontSaving>());

//Thecallbackwillexport.ttffilesandsavethemalongsidetheoutputdocument.
doc->Save(ArtifactsDir+u"HtmlSaveOptions.SaveExportedFonts.html",options);

std::function<bool(Strings)>endsWithTtf=[](Strings)
{
returns.EndsWith(u".ttf");
};

for(StringfontFilename:System::Array<String>::FindAll(System::IO::Directory::GetFiles(ArtifactsDir),endsWithTtf))
{
std::cout<<fontFilename<<std::endl;
}

}

classHandleFontSaving:publicIFontSavingCallback
{
private:
voidFontSaving(SharedPtr<FontSavingArgs>args)override
{
std::cout<<"Font:\t"<<args->get_FontFamilyName();
if(args->get_Bold())
{
std::cout<<",bold";
}
if(args->get_Italic())
{
std::cout<<",italic";
}
std::cout<<"\nSource:\t"<<args->get_OriginalFileName()<<","<<args->get_OriginalFileSize()<<"bytes\n"<<std::endl;

//Wecanalsoaccessthesourcedocumentfromhere.
ASSERT_TRUE(args->get_Document()->get_OriginalFileName().EndsWith(u"Rendering.docx"));

ASSERT_TRUE(args->get_IsExportNeeded());
ASSERT_TRUE(args->get_IsSubsettingNeeded());

//Therearetwowaysofsavinganexportedfont.
//1-Saveittoalocalfilesystemlocation:
args->set_FontFileName(args->get_OriginalFileName().Split(MakeArray<char16_t>({System::IO::Path::DirectorySeparatorChar}))->LINQ_Last());

//2-Saveittoastream:
args->set_FontStream(MakeObject<System::IO::FileStream>(
ArtifactsDir+args->get_OriginalFileName().Split(MakeArray<char16_t>({System::IO::Path::DirectorySeparatorChar}))->LINQ_Last(),
System::IO::FileMode::Create));
ASSERT_FALSE(args->get_KeepFontStreamOpen());
}
};

