//Whenwesavethedocumentasanimage,wecanpassaSaveOptionsobjectto
//selectacolormodefortheimagethatthesavingoperationwillgenerate.
//Ifwesetthe"ImageColorMode"propertyto"ImageColorMode.BlackAndWhite",
//thesavingoperationwillapplygrayscalecolorreductionwhilerenderingthedocument.
//Ifwesetthe"ImageColorMode"propertyto"ImageColorMode.Grayscale",
//thesavingoperationwillrenderthedocumentintoamonochromeimage.
//Ifwesetthe"ImageColorMode"propertyto"None",thesavingoperationwillapplythedefaultmethod
//andpreserveallthedocument'scolorsintheoutputimage.
autoimageSaveOptions=MakeObject<ImageSaveOptions>(SaveFormat::Png);
imageSaveOptions->set_ImageColorMode(imageColorMode);

doc->Save(ArtifactsDir+u"ImageSaveOptions.ColorMode.png",imageSaveOptions);

