Examples


Shows how to select the compression scheme to apply to a document that we convert into a TIFF image. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

builder->InsertImage(ImageDir+u"Logo.jpg");

//Createan"ImageSaveOptions"objectwhichwecanpasstothedocument's"Save"method
//tomodifythewayinwhichthatmethodrendersthedocumentintoanimage.
autooptions=MakeObject<ImageSaveOptions>(SaveFormat::Tiff);

//Setthe"TiffCompression"propertyto"TiffCompression.None"toapplynocompressionwhilesaving,
//whichmayresultinaverylargeoutputfile.
//Setthe"TiffCompression"propertyto"TiffCompression.Rle"toapplyRLEcompression
//Setthe"TiffCompression"propertyto"TiffCompression.Lzw"toapplyLZWcompression.
//Setthe"TiffCompression"propertyto"TiffCompression.Ccitt3"toapplyCCITT3compression.
//Setthe"TiffCompression"propertyto"TiffCompression.Ccitt4"toapplyCCITT4compression.
options->set_TiffCompression(tiffCompression);

doc->Save(ArtifactsDir+u"ImageSaveOptions.TiffImageCompression.tiff",options);

switch(tiffCompression)
{
caseTiffCompression::None:
ASSERT_LT(3000000,MakeObject<System::IO::FileInfo>(ArtifactsDir+u"ImageSaveOptions.TiffImageCompression.tiff")->get_Length());
break;

caseTiffCompression::Rle:
ASSERT_LT(600000,MakeObject<System::IO::FileInfo>(ArtifactsDir+u"ImageSaveOptions.TiffImageCompression.tiff")->get_Length());
break;

caseTiffCompression::Lzw:
ASSERT_LT(200000,MakeObject<System::IO::FileInfo>(ArtifactsDir+u"ImageSaveOptions.TiffImageCompression.tiff")->get_Length());
break;

caseTiffCompression::Ccitt3:
ASSERT_GE(90000,MakeObject<System::IO::FileInfo>(ArtifactsDir+u"ImageSaveOptions.TiffImageCompression.tiff")->get_Length());
break;

caseTiffCompression::Ccitt4:
ASSERT_GE(20000,MakeObject<System::IO::FileInfo>(ArtifactsDir+u"ImageSaveOptions.TiffImageCompression.tiff")->get_Length());
break;
}

