autobuilder=MakeObject<DocumentBuilder>(doc);

builder->get_Font()->set_Name(u"TimesNewRoman");
builder->get_Font()->set_Size(24);
builder->Writeln(u"Loremipsumdolorsitamet,consecteturadipiscingelit,seddoeiusmodtemporincididuntutlaboreetdoloremagnaaliqua.");

builder->InsertImage(ImageDir+u"Logo.jpg");

//Createan"ImageSaveOptions"objectwhichwecanpasstothedocument's"Save"method
//tomodifythewayinwhichthatmethodrendersthedocumentintoanimage.
autooptions=MakeObject<ImageSaveOptions>(SaveFormat::Png);

//Setthe"Resolution"propertyto"72"torenderthedocumentin72dpi.
options->set_Resolution(72.0f);

doc->Save(ArtifactsDir+u"ImageSaveOptions.Resolution.72dpi.png",options);

ASSERT_GE(120000,MakeObject<System::IO::FileInfo>(ArtifactsDir+u"ImageSaveOptions.Resolution.72dpi.png")->get_Length());

//Setthe"Resolution"propertyto"300"torenderthedocumentin300dpi.
options->set_Resolution(300.0f);

doc->Save(ArtifactsDir+u"ImageSaveOptions.Resolution.300dpi.png",options);

ASSERT_LT(700000,MakeObject<System::IO::FileInfo>(ArtifactsDir+u"ImageSaveOptions.Resolution.300dpi.png")->get_Length());
