Examples


Shows how to involve an image saving callback in an HTML conversion process. voidImageSavingCallback()
{
autodoc=MakeObject<Document>(MyDir+u"Rendering.docx");

//WhenwesavethedocumenttoHTML,wecanpassaSaveOptionsobjecttodesignateacallback
//tocustomizetheimagesavingprocess.
autooptions=MakeObject<HtmlSaveOptions>();
options->set_ImageSavingCallback(MakeObject<ExHtmlSaveOptions::ImageShapePrinter>());

doc->Save(ArtifactsDir+u"HtmlSaveOptions.ImageSavingCallback.html",options);
}

classImageShapePrinter:publicIImageSavingCallback
{
public:
ImageShapePrinter():mImageCount(0)
{
}

private:
intmImageCount;

voidImageSaving(SharedPtr<ImageSavingArgs>args)override
{
args->set_KeepImageStreamOpen(false);
ASSERT_TRUE(args->get_IsImageAvailable());

std::cout<<args->get_Document()->get_OriginalFileName().Split(MakeArray<char16_t>({u'\\'}))->LINQ_Last()<<"Image#"<<++mImageCount
<<std::endl;

autolayoutCollector=MakeObject<LayoutCollector>(args->get_Document());

std::cout<<"\tOnpage:\t"<<layoutCollector->GetStartPageIndex(args->get_CurrentShape())<<std::endl;
std::cout<<"\tDimensions:\t"<<args->get_CurrentShape()->get_Bounds()<<std::endl;
std::cout<<String::Format(u"\tAlignment:\t{0}",args->get_CurrentShape()->get_VerticalAlignment())<<std::endl;
std::cout<<String::Format(u"\tWraptype:\t{0}",args->get_CurrentShape()->get_WrapType())<<std::endl;
std::cout<<"Outputfilename:\t"<<args->get_ImageFileName()<<"\n"<<std::endl;
}
};

