Examples


Shows how to split a document into parts and save them. voidDocumentPartsFileNames()
{
autodoc=MakeObject<Document>(MyDir+u"Rendering.docx");
StringoutFileName=u"SavingCallback.DocumentPartsFileNames.html";

//Createan"HtmlFixedSaveOptions"object,whichwecanpasstothedocument's"Save"method
//tomodifyhowweconvertthedocumenttoHTML.
autooptions=MakeObject<HtmlSaveOptions>();

//Ifwesavethedocumentnormally,therewillbeoneoutputHTML
//documentwithallthesourcedocument'scontents.
//Setthe"DocumentSplitCriteria"propertyto"DocumentSplitCriteria.SectionBreak"to
//saveourdocumenttomultipleHTMLfiles:oneforeachsection.
options->set_DocumentSplitCriteria(DocumentSplitCriteria::SectionBreak);

//Assignacustomcallbacktothe"DocumentPartSavingCallback"propertytoalterthedocumentpartsavinglogic.
options->set_DocumentPartSavingCallback(MakeObject<ExSavingCallback::SavedDocumentPartRename>(outFileName,options->get_DocumentSplitCriteria()));

//Ifweconvertadocumentthatcontainsimagesintohtml,wewillendupwithonehtmlfilewhichlinkstoseveralimages.
//Eachimagewillbeintheformofafileinthelocalfilesystem.
//Thereisalsoacallbackthatcancustomizethenameandfilesystemlocationofeachimage.
options->set_ImageSavingCallback(MakeObject<ExSavingCallback::SavedImageRename>(outFileName));

doc->Save(ArtifactsDir+outFileName,options);
}

classSavedDocumentPartRename:publicIDocumentPartSavingCallback
{
public:
SavedDocumentPartRename(StringoutFileName,DocumentSplitCriteriadocumentSplitCriteria)
:mCount(0),mDocumentSplitCriteria(((Aspose::Words::Saving::DocumentSplitCriteria)0))
{
mOutFileName=outFileName;
mDocumentSplitCriteria=documentSplitCriteria;
}

private:
intmCount;
StringmOutFileName;
DocumentSplitCriteriamDocumentSplitCriteria;

voidDocumentPartSaving(SharedPtr<DocumentPartSavingArgs>args)override
{
//Wecanaccesstheentiresourcedocumentviathe"Document"property.
ASSERT_TRUE(args->get_Document()->get_OriginalFileName().EndsWith(u"Rendering.docx"));

StringpartType=String::Empty;

switch(mDocumentSplitCriteria)
{
caseDocumentSplitCriteria::PageBreak:
partType=u"Page";
break;

caseDocumentSplitCriteria::ColumnBreak:
partType=u"Column";
break;

caseDocumentSplitCriteria::SectionBreak:
partType=u"Section";
break;

caseDocumentSplitCriteria::HeadingParagraph:
partType=u"Paragraphfromheading";
break;

default:
break;
}

StringpartFileName=String::Format(u"{0}part{1},oftype{2}{3}",mOutFileName,++mCount,partType,
System::IO::Path::GetExtension(args->get_DocumentPartFileName()));

//BelowaretwowaysofspecifyingwhereAspose.Wordswillsaveeachpartofthedocument.
//1-Setafilenamefortheoutputpartfile:
args->set_DocumentPartFileName(partFileName);

//2-Createacustomstreamfortheoutputpartfile:
args->set_DocumentPartStream(MakeObject<System::IO::FileStream>(ArtifactsDir+partFileName,System::IO::FileMode::Create));

ASSERT_TRUE(args->get_DocumentPartStream()->get_CanWrite());
ASSERT_FALSE(args->get_KeepDocumentPartStreamOpen());
}
};

classSavedImageRename:publicIImageSavingCallback
{
public:
SavedImageRename(StringoutFileName):mCount(0)
{
mOutFileName=outFileName;
}

private:
intmCount;
StringmOutFileName;

voidImageSaving(SharedPtr<ImageSavingArgs>args)override
{
StringimageFileName=String::Format(u"{0}shape{1},oftype{2}{3}",mOutFileName,++mCount,args->get_CurrentShape()->get_ShapeType(),
System::IO::Path::GetExtension(args->get_ImageFileName()));

//BelowaretwowaysofspecifyingwhereAspose.Wordswillsaveeachpartofthedocument.
//1-Setafilenamefortheoutputimagefile:
args->set_ImageFileName(imageFileName);

//2-Createacustomstreamfortheoutputimagefile:
args->set_ImageStream(MakeObject<System::IO::FileStream>(ArtifactsDir+imageFileName,System::IO::FileMode::Create));

ASSERT_TRUE(args->get_ImageStream()->get_CanWrite());
ASSERT_TRUE(args->get_IsImageAvailable());
ASSERT_FALSE(args->get_KeepImageStreamOpen());
}
};

