detaileddescription.para:
Examples


Shows how to rename the image name during saving into Markdown document. voidRenameImages()
{
autodoc=MakeObject<Document>(MyDir+u"Rendering.docx");

autooptions=MakeObject<MarkdownSaveOptions>();

//IfweconvertadocumentthatcontainsimagesintoMarkdown,wewillendupwithoneMarkdownfilewhichlinkstoseveralimages.
//Eachimagewillbeintheformofafileinthelocalfilesystem.
//Thereisalsoacallbackthatcancustomizethenameandfilesystemlocationofeachimage.
options->set_ImageSavingCallback(MakeObject<ExDocumentBuilder::SavedImageRename>(u"DocumentBuilder.HandleDocument.md"));

//TheImageSaving()methodofourcallbackwillberunatthistime.
doc->Save(ArtifactsDir+u"DocumentBuilder.HandleDocument.md",options);

ASSERT_EQ(1,System::IO::Directory::GetFiles(ArtifactsDir)
->LINQ_Where([](Strings){returns.StartsWith(ArtifactsDir+u"DocumentBuilder.HandleDocument.mdshape");})
->LINQ_Count([](Stringf){returnf.EndsWith(u".jpeg");}));
ASSERT_EQ(8,System::IO::Directory::GetFiles(ArtifactsDir)
->LINQ_Where([](Strings){returns.StartsWith(ArtifactsDir+u"DocumentBuilder.HandleDocument.mdshape");})
->LINQ_Count([](Stringf){returnf.EndsWith(u".png");}));
}

classSavedImageRename:publicIImageSavingCallback
{
public:
SavedImageRename(StringoutFileName):mCount(0)
{
mOutFileName=outFileName;
}

private:
intmCount;
StringmOutFileName;

voidImageSaving(SharedPtr<ImageSavingArgs>args)override
{
StringimageFileName=String::Format(u"{0}shape{1},oftype{2}{3}",mOutFileName,++mCount,args->get_CurrentShape()->get_ShapeType(),
System::IO::Path::GetExtension(args->get_ImageFileName()));

args->set_ImageFileName(imageFileName);
args->set_ImageStream(MakeObject<System::IO::FileStream>(ArtifactsDir+imageFileName,System::IO::FileMode::Create));

ASSERT_TRUE(args->get_ImageStream()->get_CanWrite());
ASSERT_TRUE(args->get_IsImageAvailable());
ASSERT_FALSE(args->get_KeepImageStreamOpen());
}
};
