detaileddescription.para:
Examples


Shows how to enable or disable subsetting when embedding fonts while rendering a document to PDF. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

builder->get_Font()->set_Name(u"Arial");
builder->Writeln(u"Helloworld!");
builder->get_Font()->set_Name(u"Arvo");
builder->Writeln(u"Thequickbrownfoxjumpsoverthelazydog.");

//Configureourfontsourcestoensurethatwehaveaccesstoboththefontsinthisdocument.
ArrayPtr<SharedPtr<FontSourceBase>>originalFontsSources=FontSettings::get_DefaultInstance()->GetFontsSources();
autofolderFontSource=MakeObject<FolderFontSource>(FontsDir,true);
FontSettings::get_DefaultInstance()->SetFontsSources(MakeArray<SharedPtr<FontSourceBase>>({originalFontsSources[0],folderFontSource}));

ArrayPtr<SharedPtr<FontSourceBase>>fontSources=FontSettings::get_DefaultInstance()->GetFontsSources();
ASSERT_TRUE(fontSources[0]->GetAvailableFonts()->LINQ_Any([](SharedPtr<PhysicalFontInfo>f){returnf->get_FullFontName()==u"Arial";}));
ASSERT_TRUE(fontSources[1]->GetAvailableFonts()->LINQ_Any([](SharedPtr<PhysicalFontInfo>f){returnf->get_FullFontName()==u"Arvo";}));

//Createa"PdfSaveOptions"objectthatwecanpasstothedocument's"Save"method
//tomodifyhowthatmethodconvertsthedocumentto.PDF.
autooptions=MakeObject<PdfSaveOptions>();

//Sinceourdocumentcontainsacustomfont,embeddingintheoutputdocumentmaybedesirable.
//Setthe"EmbedFullFonts"propertyto"true"toembedeveryglyphofeveryembeddedfontintheoutputPDF.
//Thedocument'ssizemaybecomeverylarge,butwewillhavefulluseofallfontsifweeditthePDF.
//Setthe"EmbedFullFonts"propertyto"false"toapplysubsettingtofonts,savingonlytheglyphs
//thatthedocumentisusing.Thefilewillbeconsiderablysmaller,
//butwemayneedaccesstoanycustomfontsifweeditthedocument.
options->set_EmbedFullFonts(embedFullFonts);

doc->Save(ArtifactsDir+u"PdfSaveOptions.EmbedFullFonts.pdf",options);

if(embedFullFonts)
{
ASSERT_LT(500000,MakeObject<System::IO::FileInfo>(ArtifactsDir+u"PdfSaveOptions.EmbedFullFonts.pdf")->get_Length());
}
else
{
ASSERT_GE(25000,MakeObject<System::IO::FileInfo>(ArtifactsDir+u"PdfSaveOptions.EmbedFullFonts.pdf")->get_Length());
}

//Restoretheoriginalfontsources.
FontSettings::get_DefaultInstance()->SetFontsSources(originalFontsSources);

