Examples


Shows how to sign a generated PDF document. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);
builder->Writeln(u"ContentsofsignedPDF.");

SharedPtr<CertificateHolder>certificateHolder=CertificateHolder::Create(MyDir+u"morzal.pfx",u"aw");

//Createa"PdfSaveOptions"objectthatwecanpasstothedocument's"Save"method
//tomodifyhowthatmethodconvertsthedocumentto.PDF.
autooptions=MakeObject<PdfSaveOptions>();

//Configurethe"DigitalSignatureDetails"objectofthe"SaveOptions"objectto
//digitallysignthedocumentaswerenderitwiththe"Save"method.
System::DateTimesigningTime=System::DateTime::get_Now();
options->set_DigitalSignatureDetails(MakeObject<PdfDigitalSignatureDetails>(certificateHolder,u"TestSigning",u"MyOffice",signingTime));
options->get_DigitalSignatureDetails()->set_HashAlgorithm(PdfDigitalSignatureHashAlgorithm::Sha256);

ASSERT_EQ(u"TestSigning",options->get_DigitalSignatureDetails()->get_Reason());
ASSERT_EQ(u"MyOffice",options->get_DigitalSignatureDetails()->get_Location());
ASSERT_EQ(signingTime.ToUniversalTime(),options->get_DigitalSignatureDetails()->get_SignatureDate().ToUniversalTime());

doc->Save(ArtifactsDir+u"PdfSaveOptions.PdfDigitalSignature.pdf",options);

