Shows how to change the resolution of images in the PDF document. autodoc=MakeObject<Document>(MyDir+u"Images.docx");

//Createa"PdfSaveOptions"objectthatwecanpasstothedocument's"Save"method
//tomodifyhowthatmethodconvertsthedocumentto.PDF.
autooptions=MakeObject<PdfSaveOptions>();

//Bydefault,Aspose.WordsdownsampleallimagesinadocumentthatwesavetoPDFto220ppi.
ASSERT_TRUE(options->get_DownsampleOptions()->get_DownsampleImages());
ASSERT_EQ(220,options->get_DownsampleOptions()->get_Resolution());
ASSERT_EQ(0,options->get_DownsampleOptions()->get_ResolutionThreshold());

doc->Save(ArtifactsDir+u"PdfSaveOptions.DownsampleOptions.Default.pdf",options);

//Setthe"Resolution"propertyto"36"todownsampleallimagesto36ppi.
options->get_DownsampleOptions()->set_Resolution(36);

//Setthe"ResolutionThreshold"propertytoonlyapplythedownsamplingto
//imageswitharesolutionthatisabove128ppi.
options->get_DownsampleOptions()->set_ResolutionThreshold(128);

//Onlythefirsttwoimagesfromthedocumentwillbedownsampledatthisstage.
doc->Save(ArtifactsDir+u"PdfSaveOptions.DownsampleOptions.LowerResolution.pdf",options);

