Examples


Shows how to set Aspose.Words to skip embedding Arial and Times New Roman fonts into a PDF document. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

//"Arial"isastandardfont,and"CourierNew"isanonstandardfont.
builder->get_Font()->set_Name(u"Arial");
builder->Writeln(u"Helloworld!");
builder->get_Font()->set_Name(u"CourierNew");
builder->Writeln(u"Thequickbrownfoxjumpsoverthelazydog.");

//Createa"PdfSaveOptions"objectthatwecanpasstothedocument's"Save"method
//tomodifyhowthatmethodconvertsthedocumentto.PDF.
autooptions=MakeObject<PdfSaveOptions>();

//Setthe"EmbedFullFonts"propertyto"true"toembedeveryglyphofeveryembeddedfontintheoutputPDF.
options->set_EmbedFullFonts(true);

//Setthe"FontEmbeddingMode"propertyto"EmbedAll"toembedallfontsintheoutputPDF.
//Setthe"FontEmbeddingMode"propertyto"EmbedNonstandard"toonlyallownonstandardfonts'embeddingintheoutputPDF.
//Setthe"FontEmbeddingMode"propertyto"EmbedNone"tonotembedanyfontsintheoutputPDF.
options->set_FontEmbeddingMode(pdfFontEmbeddingMode);

doc->Save(ArtifactsDir+u"PdfSaveOptions.EmbedWindowsFonts.pdf",options);

switch(pdfFontEmbeddingMode)
{
casePdfFontEmbeddingMode::EmbedAll:
ASSERT_LT(1000000,MakeObject<System::IO::FileInfo>(ArtifactsDir+u"PdfSaveOptions.EmbedWindowsFonts.pdf")->get_Length());
break;

casePdfFontEmbeddingMode::EmbedNonstandard:
ASSERT_LT(480000,MakeObject<System::IO::FileInfo>(ArtifactsDir+u"PdfSaveOptions.EmbedWindowsFonts.pdf")->get_Length());
break;

casePdfFontEmbeddingMode::EmbedNone:
ASSERT_GE(4217,MakeObject<System::IO::FileInfo>(ArtifactsDir+u"PdfSaveOptions.EmbedWindowsFonts.pdf")->get_Length());
break;
}

