builder->get_Font()->set_Name(u"CourierNew");
builder->Writeln(u"Thequickbrownfoxjumpsoverthelazydog.");

//Createa"PdfSaveOptions"objectthatwecanpasstothedocument's"Save"method
//tomodifyhowthatmethodconvertsthedocumentto.PDF.
autooptions=MakeObject<PdfSaveOptions>();

//Setthe"UseCoreFonts"propertyto"true"toreplacesomefonts,
//includingthetwofontsinourdocument,withtheirPDFType1equivalents.
//Setthe"UseCoreFonts"propertyto"false"tonotapplyPDFType1fonts.
options->set_UseCoreFonts(useCoreFonts);

doc->Save(ArtifactsDir+u"PdfSaveOptions.EmbedCoreFonts.pdf",options);

if(useCoreFonts)
{
ASSERT_GE(3000,MakeObject<System::IO::FileInfo>(ArtifactsDir+u"PdfSaveOptions.EmbedCoreFonts.pdf")->get_Length());
}
else
{
ASSERT_LT(30000,MakeObject<System::IO::FileInfo>(ArtifactsDir+u"PdfSaveOptions.EmbedCoreFonts.pdf")->get_Length());
}

