Examples


Shows how to use a callback to track external resources created while converting a document to HTML. voidResourceSavingCallback()
{
autodoc=MakeObject<Document>(MyDir+u"Bulletpointswithalternativefont.docx");

autocallback=MakeObject<ExHtmlFixedSaveOptions::FontSavingCallback>();

autosaveOptions=MakeObject<HtmlFixedSaveOptions>();
saveOptions->set_ResourceSavingCallback(callback);

doc->Save(ArtifactsDir+u"HtmlFixedSaveOptions.UsingMachineFonts.html",saveOptions);

std::cout<<callback->GetText()<<std::endl;
}

classFontSavingCallback:publicIResourceSavingCallback
{
public:
voidResourceSaving(SharedPtr<ResourceSavingArgs>args)override
{
mText->AppendLine(String::Format(u"OriginaldocumentURI:\t{0}",args->get_Document()->get_OriginalFileName()));
mText->AppendLine(String::Format(u"Resourcebeingsaved:\t{0}",args->get_ResourceFileName()));
mText->AppendLine(String::Format(u"Fulluriaftersaving:\t{0}\n",args->get_ResourceFileUri()));
}

StringGetText()
{
returnmText->ToString();
}

FontSavingCallback():mText(MakeObject<System::Text::StringBuilder>())
{
}

private:
SharedPtr<System::Text::StringBuilder>mText;
};

Aspose::Words::Saving::ResourceSavingArgs::get_ResourceStream

Examples


Shows how to use a callback to print the URIs of external resources created while converting a document to HTML. voidHtmlFixedResourceFolder()
{
autodoc=MakeObject<Document>(MyDir+u"Rendering.docx");

autocallback=MakeObject<ExHtmlFixedSaveOptions::ResourceUriPrinter>();

autooptions=MakeObject<HtmlFixedSaveOptions>();
options->set_SaveFormat(SaveFormat::HtmlFixed);
options->set_ExportEmbeddedImages(false);
options->set_ResourcesFolder(ArtifactsDir+u"HtmlFixedResourceFolder");
options->set_ResourcesFolderAlias(ArtifactsDir+u"HtmlFixedResourceFolderAlias");
options->set_ShowPageBorder(false);
options->set_ResourceSavingCallback(callback);

//AfolderspecifiedbyResourcesFolderAliaswillcontaintheresourcesinsteadofResourcesFolder.
//Wemustensurethefolderexistsbeforethestreamscanputtheirresourcesintoit.
System::IO::Directory::CreateDirectory_(options->get_ResourcesFolderAlias());

doc->Save(ArtifactsDir+u"HtmlFixedSaveOptions.HtmlFixedResourceFolder.html",options);

std::cout<<callback->GetText()<<std::endl;

ArrayPtr<String>resourceFiles=System::IO::Directory::GetFiles(ArtifactsDir+u"HtmlFixedResourceFolderAlias");

ASSERT_FALSE(System::IO::Directory::Exists(ArtifactsDir+u"HtmlFixedResourceFolder"));
std::function<bool(Stringf)>isImageOrCss=[](Stringf)
{
returnf.EndsWith(u".jpeg")||f.EndsWith(u".png")||f.EndsWith(u".css");
};

ASSERT_EQ(6,resourceFiles->LINQ_Count(isImageOrCss));
}

classResourceUriPrinter:publicIResourceSavingCallback
{
public:
StringGetText()
{
returnmText->ToString();
}

ResourceUriPrinter():mSavedResourceCount(0),mText(MakeObject<System::Text::StringBuilder>())
{
}

private:
intmSavedResourceCount;
SharedPtr<System::Text::StringBuilder>mText;

voidResourceSaving(SharedPtr<ResourceSavingArgs>args)override
{
//IfwesetafolderaliasintheSaveOptionsobject,wewillbeabletoprintitfromhere.
mText->AppendLine(String::Format(u"Resource#{0}\"{1}\"",++mSavedResourceCount,args->get_ResourceFileName()));

Stringextension=System::IO::Path::GetExtension(args->get_ResourceFileName());
if(extension==u".ttf"||extension==u".woff")
{
//Bydefault,'ResourceFileUri'usessystemfolderforfonts.
//Toavoidproblemsinotherplatformsyoumustexplicitlyspecifythepathforthefonts.
args->set_ResourceFileUri(ArtifactsDir+System::IO::Path::DirectorySeparatorChar+args->get_ResourceFileName());
}

mText->AppendLine(String(u"\t")+args->get_ResourceFileUri());

//Ifwehavespecifiedafolderinthe"ResourcesFolderAlias"property,
//wewillalsoneedtoredirecteachstreamtoputitsresourceinthatfolder.
args->set_ResourceStream(MakeObject<System::IO::FileStream>(args->get_ResourceFileUri(),System::IO::FileMode::Create));
args->set_KeepResourceStreamOpen(false);
}
};

