Examples


Shows how to convert all images in a document to the Windows Metafile format as we save the document as an RTF. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

builder->Writeln(u"Jpegimage:");
SharedPtr<Shape>imageShape=builder->InsertImage(ImageDir+u"Logo.jpg");

ASSERT_EQ(ImageType::Jpeg,imageShape->get_ImageData()->get_ImageType());

builder->InsertParagraph();
builder->Writeln(u"Pngimage:");
imageShape=builder->InsertImage(ImageDir+u"Transparentbackgroundlogo.png");

ASSERT_EQ(ImageType::Png,imageShape->get_ImageData()->get_ImageType());

//Createan"RtfSaveOptions"objecttopasstothedocument's"Save"methodtomodifyhowwesaveittoanRTF.
autortfSaveOptions=MakeObject<RtfSaveOptions>();

//Setthe"SaveImagesAsWmf"propertyto"true"toconvertallimagesinthedocumenttoWMFaswesaveittoRTF.
//DoingsowillhelpreaderssuchasWordPadtoreadourdocument.
//Setthe"SaveImagesAsWmf"propertyto"false"topreservetheoriginalformatofallimagesinthedocument
//aswesaveittoRTF.ThiswillpreservethequalityoftheimagesatthecostofcompatibilitywitholderRTFreaders.
rtfSaveOptions->set_SaveImagesAsWmf(saveImagesAsWmf);

doc->Save(ArtifactsDir+u"RtfSaveOptions.SaveImagesAsWmf.rtf",rtfSaveOptions);

doc=MakeObject<Document>(ArtifactsDir+u"RtfSaveOptions.SaveImagesAsWmf.rtf");

SharedPtr<NodeCollection>shapes=doc->GetChildNodes(NodeType::Shape,true);

if(saveImagesAsWmf)
{
ASSERT_EQ(ImageType::Wmf,(System::DynamicCast<Shape>(shapes->idx_get(0)))->get_ImageData()->get_ImageType());
ASSERT_EQ(ImageType::Wmf,(System::DynamicCast<Shape>(shapes->idx_get(1)))->get_ImageData()->get_ImageType());
}
else
{
ASSERT_EQ(ImageType::Jpeg,(System::DynamicCast<Shape>(shapes->idx_get(0)))->get_ImageData()->get_ImageType());
ASSERT_EQ(ImageType::Png,(System::DynamicCast<Shape>(shapes->idx_get(1)))->get_ImageData()->get_ImageType());
}

