Examples


Shows how to save the document with PostScript font. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

builder->get_Font()->set_Name(u"PostScriptFont");
builder->Writeln(u"SometextwithPostScriptfont.");

//LoadthefontwithPostScripttouseinthedocument.
autootf=MakeObject<MemoryFontSource>(System::IO::File::ReadAllBytes(FontsDir+u"AllegroOpen.otf"));
doc->set_FontSettings(MakeObject<FontSettings>());
doc->get_FontSettings()->SetFontsSources(MakeArray<SharedPtr<FontSourceBase>>({otf}));

//EmbedTrueTypefonts.
doc->get_FontInfos()->set_EmbedTrueTypeFonts(true);

//AllowembeddingPostScriptfontswhileembeddingTrueTypefonts.
//MicrosoftWorddoesnotembedPostScriptfonts,butcanopendocumentswithembeddedfontsofthistype.
SharedPtr<SaveOptions>saveOptions=SaveOptions::CreateSaveOptions(SaveFormat::Docx);
saveOptions->set_AllowEmbeddingPostScriptFonts(true);

doc->Save(ArtifactsDir+u"Document.AllowEmbeddingPostScriptFonts.docx",saveOptions);

