Examples


Shows how to enhance the readability of the raw code of a saved .html document. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);
builder->Writeln(u"Helloworld!");

autohtmlOptions=MakeObject<HtmlSaveOptions>(SaveFormat::Html);
htmlOptions->set_PrettyFormat(usePrettyFormat);

doc->Save(ArtifactsDir+u"HtmlSaveOptions.PrettyFormat.html",htmlOptions);

//Enablingprettyformatmakestherawhtmlcodemorereadablebyaddingtabstopandnewlinecharacters.
Stringhtml=System::IO::File::ReadAllText(ArtifactsDir+u"HtmlSaveOptions.PrettyFormat.html");

if(usePrettyFormat)
{
ASSERT_EQ(String(u"<html>\r\n")+u"\t<head>\r\n"+u"\t\t<metahttp-equiv=\"Content-Type\"content=\"text/html;charset=utf-8\"/>\r\n"+
u"\t\t<metahttp-equiv=\"Content-Style-Type\"content=\"text/css\"/>\r\n"+
String::Format(u"\t\t<metaname=\"generator\"content=\"{0}{1}\"/>\r\n",BuildVersionInfo::get_Product(),
BuildVersionInfo::get_Version())+
u"\t\t<title></title>\r\n"+u"\t</head>\r\n"+u"\t<bodystyle=\"font-family:'TimesNewRoman';font-size:12pt\">\r\n"+
u"\t\t<div>\r\n"+u"\t\t\t<pstyle=\"margin-top:0pt;margin-bottom:0pt\">\r\n"+u"\t\t\t\t<span>Helloworld!</span>\r\n"+
u"\t\t\t</p>\r\n"+u"\t\t\t<pstyle=\"margin-top:0pt;margin-bottom:0pt\">\r\n"+
u"\t\t\t\t<spanstyle=\"-aw-import:ignore\">&#xa0;</span>\r\n"+u"\t\t\t</p>\r\n"+u"\t\t</div>\r\n"+u"\t</body>\r\n</html>\r\n",
html);
}
else
{
ASSERT_EQ(String(u"<html><head><metahttp-equiv=\"Content-Type\"content=\"text/html;charset=utf-8\"/>")+
u"<metahttp-equiv=\"Content-Style-Type\"content=\"text/css\"/>"+
String::Format(u"<metaname=\"generator\"content=\"{0}{1}\"/><title></title></head>",BuildVersionInfo::get_Product(),
BuildVersionInfo::get_Version())+
u"<bodystyle=\"font-family:'TimesNewRoman';font-size:12pt\">"+
u"<div><pstyle=\"margin-top:0pt;margin-bottom:0pt\"><span>Helloworld!</span></p>"+
u"<pstyle=\"margin-top:0pt;margin-bottom:0pt\"><spanstyle=\"-aw-import:ignore\">&#xa0;</span></p></div></body></html>",
html);
}

