Shows how to update structured document tags while saving a document to PDF. autodoc=MakeObject<Document>();

//Insertadrop-downliststructureddocumenttag.
autotag=MakeObject<StructuredDocumentTag>(doc,SdtType::DropDownList,MarkupLevel::Block);
tag->get_ListItems()->Add(MakeObject<SdtListItem>(u"Value1"));
tag->get_ListItems()->Add(MakeObject<SdtListItem>(u"Value2"));
tag->get_ListItems()->Add(MakeObject<SdtListItem>(u"Value3"));

//Thedrop-downlistcurrentlydisplays"Chooseanitem"asthedefaulttext.
//Setthe"SelectedValue"propertytooneofthelistitemstogetthetagto
//displaythatlistitem'svalueinsteadofthedefaulttext.
tag->get_ListItems()->set_SelectedValue(tag->get_ListItems()->idx_get(1));

doc->get_FirstSection()->get_Body()->AppendChild(tag);

//Createa"PdfSaveOptions"objecttopasstothedocument's"Save"method
//tomodifyhowthatmethodsavesthedocumentto.PDF.
autooptions=MakeObject<PdfSaveOptions>();

//Setthe"UpdateSdtContent"propertyto"false"nottoupdatethestructureddocumenttags
//whilesavingthedocumenttoPDF.Theywilldisplaytheirdefaultvaluesastheywereatthetimeofconstruction.
//Setthe"UpdateSdtContent"propertyto"true"tomakesurethetagsdisplayupdatedvaluesinthePDF.
options->set_UpdateSdtContent(updateSdtContent);

doc->Save(ArtifactsDir+u"StructuredDocumentTag.UpdateSdtContent.pdf",options);

