detaileddescription.para:
Examples


Shows how to print the URIs of linked resources created while converting a document to fixed-form .xaml. voidResourceFolder()
{
autodoc=MakeObject<Document>(MyDir+u"Rendering.docx");
autocallback=MakeObject<ExXamlFixedSaveOptions::ResourceUriPrinter>();

//Createa"XamlFixedSaveOptions"object,whichwecanpasstothedocument's"Save"method
//tomodifyhowwesavethedocumenttotheXAMLsaveformat.
autooptions=MakeObject<XamlFixedSaveOptions>();

ASSERT_EQ(SaveFormat::XamlFixed,options->get_SaveFormat());

//Usethe"ResourcesFolder"propertytoassignafolderinthelocalfilesystemintowhich
//Aspose.Wordswillsaveallthedocument'slinkedresources,suchasimagesandfonts.
options->set_ResourcesFolder(ArtifactsDir+u"XamlFixedResourceFolder");

//Usethe"ResourcesFolderAlias"propertytousethisfolder
//whenconstructingimageURIsinsteadoftheresourcesfolder'sname.
options->set_ResourcesFolderAlias(ArtifactsDir+u"XamlFixedFolderAlias");

options->set_ResourceSavingCallback(callback);

//Afolderspecifiedby"ResourcesFolderAlias"willneedtocontaintheresourcesinsteadof"ResourcesFolder".
//Wemustensurethefolderexistsbeforethecallback'sstreamscanputtheirresourcesintoit.
System::IO::Directory::CreateDirectory_(options->get_ResourcesFolderAlias());

doc->Save(ArtifactsDir+u"XamlFixedSaveOptions.ResourceFolder.xaml",options);

for(constauto&resource:callback->get_Resources())
{
std::cout<<resource<<std::endl;
}
}

classResourceUriPrinter:publicIResourceSavingCallback
{
public:
SharedPtr<System::Collections::Generic::List<String>>get_Resources()
{
returnpr_Resources;
}

ResourceUriPrinter()
{
pr_Resources=MakeObject<System::Collections::Generic::List<String>>();
}

private:
SharedPtr<System::Collections::Generic::List<String>>pr_Resources;

voidResourceSaving(SharedPtr<ResourceSavingArgs>args)override
{
get_Resources()->Add(String::Format(u"Resource\"{0}\"\n\t{1}",args->get_ResourceFileName(),args->get_ResourceFileUri()));

//Ifwespecifiedaresourcefolderalias,wewouldalsoneed
//toredirecteachstreamtoputitsresourceinthealiasfolder.
args->set_ResourceStream(MakeObject<System::IO::FileStream>(args->get_ResourceFileUri(),System::IO::FileMode::Create));
args->set_KeepResourceStreamOpen(false);
}
};

