detaileddescription.para:
Examples


Shows how to print the filenames of linked images created while converting a document to flow-form .xaml. voidImageFolder()
{
autodoc=MakeObject<Document>(MyDir+u"Rendering.docx");

autocallback=MakeObject<ExXamlFlowSaveOptions::ImageUriPrinter>(ArtifactsDir+u"XamlFlowImageFolderAlias");

//Createa"XamlFlowSaveOptions"object,whichwecanpasstothedocument's"Save"method
//tomodifyhowwesavethedocumenttotheXAMLsaveformat.
autooptions=MakeObject<XamlFlowSaveOptions>();

ASSERT_EQ(SaveFormat::XamlFlow,options->get_SaveFormat());

//Usethe"ImagesFolder"propertytoassignafolderinthelocalfilesystemintowhich
//Aspose.Wordswillsaveallthedocument'slinkedimages.
options->set_ImagesFolder(ArtifactsDir+u"XamlFlowImageFolder");

//Usethe"ImagesFolderAlias"propertytousethisfolder
//whenconstructingimageURIsinsteadoftheimagesfolder'sname.
options->set_ImagesFolderAlias(ArtifactsDir+u"XamlFlowImageFolderAlias");

options->set_ImageSavingCallback(callback);

//Afolderspecifiedby"ImagesFolderAlias"willneedtocontaintheresourcesinsteadof"ImagesFolder".
//Wemustensurethefolderexistsbeforethecallback'sstreamscanputtheirresourcesintoit.
System::IO::Directory::CreateDirectory_(options->get_ImagesFolderAlias());

doc->Save(ArtifactsDir+u"XamlFlowSaveOptions.ImageFolder.xaml",options);

for(constauto&resource:callback->get_Resources())
{
std::cout<<callback->get_ImagesFolderAlias()<<"/"<<resource<<std::endl;
}
}

classImageUriPrinter:publicIImageSavingCallback
{
public:
Stringget_ImagesFolderAlias()
{
returnpr_ImagesFolderAlias;
}

SharedPtr<System::Collections::Generic::List<String>>get_Resources()
{
returnpr_Resources;
}

ImageUriPrinter(StringimagesFolderAlias)
{
pr_ImagesFolderAlias=imagesFolderAlias;
pr_Resources=MakeObject<System::Collections::Generic::List<String>>();
}

private:
Stringpr_ImagesFolderAlias;
SharedPtr<System::Collections::Generic::List<String>>pr_Resources;

voidImageSaving(SharedPtr<ImageSavingArgs>args)override
{
get_Resources()->Add(args->get_ImageFileName());

//Ifwespecifiedanimagefolderalias,wewouldalsoneed
//toredirecteachstreamtoputitsimageinthealiasfolder.
args->set_ImageStream(MakeObject<System::IO::FileStream>(String::Format(u"{0}/{1}",get_ImagesFolderAlias(),args->get_ImageFileName()),
System::IO::FileMode::Create));
args->set_KeepImageStreamOpen(false);
}
};

saveFormat



Can be XamlFlow or XamlFlowPack.



