//Ournewcelldoesnothaveanyparagraphs,andwecannotaddcontentssuchasrunandshapenodestoituntilitdoes.
ASSERT_EQ(0,cell->GetChildNodes(NodeType::Any,true)->get_Count());

//Callingthe"EnsureMinimum"methodonacellwillensurethat
//thecellhasatleastoneemptyparagraph,whichwecanthenaddcontentsto.
cell->EnsureMinimum();
cell->get_FirstParagraph()->AppendChild(MakeObject<Run>(doc,u"Helloworld!"));

