classTableStructurePrinter:publicDocumentVisitor
{
public:
TableStructurePrinter():mVisitorIsInsideTable(false),mDocTraversalDepth(0)
{
mVisitedTables=MakeObject<System::Text::StringBuilder>();
mVisitorIsInsideTable=false;
}

StringGetText()
{
returnmVisitedTables->ToString();
}

VisitorActionVisitRun(SharedPtr<Run>run)override
{
if(mVisitorIsInsideTable)
{
IndentAndAppendLine(String(u"[Run]\"")+run->GetText()+u"\"");
}

returnVisitorAction::Continue;
}

VisitorActionVisitTableStart(SharedPtr<Table>table)override
{
introws=0;
intcolumns=0;

if(table->get_Rows()->get_Count()>0)
{
rows=table->get_Rows()->get_Count();
columns=table->get_FirstRow()->get_Count();
}

IndentAndAppendLine(String(u"[Tablestart]Size:")+rows+u"x"+columns);
mDocTraversalDepth++;
mVisitorIsInsideTable=true;

returnVisitorAction::Continue;
}

VisitorActionVisitTableEnd(SharedPtr<Table>table)override
{
mDocTraversalDepth--;
IndentAndAppendLine(u"[Tableend]");
mVisitorIsInsideTable=false;

returnVisitorAction::Continue;
}

VisitorActionVisitRowStart(SharedPtr<Row>row)override
{
StringrowContents=row->GetText().TrimEnd(MakeArray<char16_t>({u'\x0007',u''})).Replace(u"\u0007",u",");
introwWidth=row->IndexOf(row->get_LastCell())+1;
introwIndex=row->get_ParentTable()->IndexOf(row);
StringrowStatusInTable=row->get_IsFirstRow()&&row->get_IsLastRow()?u"only"
:row->get_IsFirstRow()?u"first"
:row->get_IsLastRow()?String(u"last")
:String(u"");
if(rowStatusInTable!=u"")
{
rowStatusInTable=String::Format(u",the{0}rowinthistable,",rowStatusInTable);
}

IndentAndAppendLine(String::Format(u"[Rowstart]Row#{0}{1}width{2},\"{3}\"",++rowIndex,rowStatusInTable,rowWidth,rowContents));
mDocTraversalDepth++;

returnVisitorAction::Continue;
}

VisitorActionVisitRowEnd(SharedPtr<Row>row)override
{
mDocTraversalDepth--;
IndentAndAppendLine(u"[Rowend]");

returnVisitorAction::Continue;
}

VisitorActionVisitCellStart(SharedPtr<Cell>cell)override
{
SharedPtr<Row>row=cell->get_ParentRow();
SharedPtr<Table>table=row->get_ParentTable();
StringcellStatusInRow=cell->get_IsFirstCell()&&cell->get_IsLastCell()?u"only"
:cell->get_IsFirstCell()?u"first"
:cell->get_IsLastCell()?String(u"last")
:String(u"");
if(cellStatusInRow!=u"")
{
cellStatusInRow=String::Format(u",the{0}cellinthisrow",cellStatusInRow);
}

IndentAndAppendLine(String::Format(u"[Cellstart]Row{0},Col{1}{2}",table->IndexOf(row)+1,row->IndexOf(cell)+1,cellStatusInRow));
mDocTraversalDepth++;

returnVisitorAction::Continue;
}

VisitorActionVisitCellEnd(SharedPtr<Cell>cell)override
{
mDocTraversalDepth--;
IndentAndAppendLine(u"[Cellend]");
returnVisitorAction::Continue;
}

private:
boolmVisitorIsInsideTable;
intmDocTraversalDepth;
SharedPtr<System::Text::StringBuilder>mVisitedTables;

voidIndentAndAppendLine(Stringtext)
{
for(inti=0;i<mDocTraversalDepth;i++)
{
mVisitedTables->Append(u"|");
}

mVisitedTables->AppendLine(text);
}
};

