u"seddoeiusmodtemporincididuntutlaboreetdoloremagnaaliqua.");

builder->InsertCell();
builder->get_CellFormat()->set_PreferredWidth(PreferredWidth::Auto());
builder->Write(String(u"Loremipsumdolorsitamet,consecteturadipiscingelit,")+
u"seddoeiusmodtemporincididuntutlaboreetdoloremagnaaliqua.");
builder->EndRow();
builder->EndTable();

//Setthe"AllowAutoFit"propertyto"false"togetthetabletomaintainthedimensions
//ofallitsrowsandcells,andtruncatecontentsiftheygettoolargetofit.
//Setthe"AllowAutoFit"propertyto"true"toallowthetabletochangeitscells'widthandheight
//toaccommodatetheircontents.
table->set_AllowAutoFit(allowAutoFit);

doc->Save(ArtifactsDir+u"Table.AllowAutoFitOnTable.html");

