Shows how to create a formatted table using DocumentBuilder. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

SharedPtr<Table>table=builder->StartTable();
builder->InsertCell();
table->set_LeftIndent(20);

//Setsomeformattingoptionsfortextandtableappearance.
builder->get_RowFormat()->set_Height(40);
builder->get_RowFormat()->set_HeightRule(HeightRule::AtLeast);
builder->get_CellFormat()->get_Shading()->set_BackgroundPatternColor(System::Drawing::Color::FromArgb(198,217,241));

builder->get_ParagraphFormat()->set_Alignment(ParagraphAlignment::Center);
builder->get_Font()->set_Size(16);
builder->get_Font()->set_Name(u"Arial");
builder->get_Font()->set_Bold(true);

//Configuringtheformattingoptionsinadocumentbuilderwillapplythem
//tothecurrentcell/rowitscursorisin,
//aswellasanynewcellsandrowscreatedusingthatbuilder.
builder->Write(u"HeaderRow,\nCell1");
builder->InsertCell();
builder->Write(u"HeaderRow,\nCell2");
builder->InsertCell();
builder->Write(u"HeaderRow,\nCell3");
builder->EndRow();

//Reconfigurethebuilder'sformattingobjectsfornewrowsandcellsthatweareabouttomake.
//Thebuilderwillnotapplythesetothefirstrowalreadycreatedsothatitwillstandoutasaheaderrow.
builder->get_CellFormat()->get_Shading()->set_BackgroundPatternColor(System::Drawing::Color::get_White());
builder->get_CellFormat()->set_VerticalAlignment(CellVerticalAlignment::Center);
builder->get_RowFormat()->set_Height(30);
builder->get_RowFormat()->set_HeightRule(HeightRule::Auto);
builder->InsertCell();
builder->get_Font()->set_Size(12);
builder->get_Font()->set_Bold(false);

builder->Write(u"Row1,Cell1.");
builder->InsertCell();
builder->Write(u"Row1,Cell2.");
builder->InsertCell();
builder->Write(u"Row1,Cell3.");
builder->EndRow();
builder->InsertCell();
builder->Write(u"Row2,Cell1.");
builder->InsertCell();
builder->Write(u"Row2,Cell2.");
builder->InsertCell();
builder->Write(u"Row2,Cell3.");
builder->EndRow();
builder->EndTable();

doc->Save(ArtifactsDir+u"DocumentBuilder.CreateFormattedTable.docx");

