Examples


Shows how to build a formatted 2x2 table. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

SharedPtr<Table>table=builder->StartTable();
builder->InsertCell();
builder->get_CellFormat()->set_VerticalAlignment(CellVerticalAlignment::Center);
builder->Write(u"Row1,cell1.");
builder->InsertCell();
builder->Write(u"Row1,cell2.");
builder->EndRow();

//Whilebuildingthetable,thedocumentbuilderwillapplyitscurrentRowFormat/CellFormatpropertyvalues
//tothecurrentrow/cellthatitscursorisinandanynewrows/cellsasitcreatesthem.
ASSERT_EQ(CellVerticalAlignment::Center,table->get_Rows()->idx_get(0)->get_Cells()->idx_get(0)->get_CellFormat()->get_VerticalAlignment());
ASSERT_EQ(CellVerticalAlignment::Center,table->get_Rows()->idx_get(0)->get_Cells()->idx_get(1)->get_CellFormat()->get_VerticalAlignment());

builder->InsertCell();
builder->get_RowFormat()->set_Height(100);
builder->get_RowFormat()->set_HeightRule(HeightRule::Exactly);
builder->get_CellFormat()->set_Orientation(TextOrientation::Upward);
builder->Write(u"Row2,cell1.");
builder->InsertCell();
builder->get_CellFormat()->set_Orientation(TextOrientation::Downward);
builder->Write(u"Row2,cell2.");
builder->EndRow();
builder->EndTable();

//Previouslyaddedrowsandcellsarenotretroactivelyaffectedbychangestothebuilder'sformatting.
ASPOSE_ASSERT_EQ(0,table->get_Rows()->idx_get(0)->get_RowFormat()->get_Height());
ASSERT_EQ(HeightRule::Auto,table->get_Rows()->idx_get(0)->get_RowFormat()->get_HeightRule());
ASPOSE_ASSERT_EQ(100,table->get_Rows()->idx_get(1)->get_RowFormat()->get_Height());
ASSERT_EQ(HeightRule::Exactly,table->get_Rows()->idx_get(1)->get_RowFormat()->get_HeightRule());
ASSERT_EQ(TextOrientation::Upward,table->get_Rows()->idx_get(1)->get_Cells()->idx_get(0)->get_CellFormat()->get_Orientation());
ASSERT_EQ(TextOrientation::Downward,table->get_Rows()->idx_get(1)->get_Cells()->idx_get(1)->get_CellFormat()->get_Orientation());

doc->Save(ArtifactsDir+u"DocumentBuilder.BuildTable.docx");

Shows how to create a table. autodoc=MakeObject<Document>();
autotable=MakeObject<Table>(doc);
doc->get_FirstSection()->get_Body()->AppendChild(table);

//Tablescontainrows,whichcontaincells,whichmayhaveparagraphs
//withtypicalelementssuchasruns,shapes,andevenothertables.
//Callingthe"EnsureMinimum"methodonatablewillensurethat
//thetablehasatleastonerow,cell,andparagraph.
autofirstRow=MakeObject<Row>(doc);
table->AppendChild(firstRow);

autofirstCell=MakeObject<Cell>(doc);
firstRow->AppendChild(firstCell);

autoparagraph=MakeObject<Paragraph>(doc);
firstCell->AppendChild(paragraph);

//Addtexttothefirstcallinthefirstrowofthetable.
autorun=MakeObject<Run>(doc,u"Helloworld!");
paragraph->AppendChild(run);

doc->Save(ArtifactsDir+u"Table.CreateTable.docx");

Shows how to iterate through all tables in the document and print the contents of each cell. autodoc=MakeObject<Document>(MyDir+u"Tables.docx");
SharedPtr<TableCollection>tables=doc->get_FirstSection()->get_Body()->get_Tables();

ASSERT_EQ(2,tables->ToArray()->get_Length());

for(inti=0;i<tables->get_Count();i++)
{
std::cout<<"StartofTable"<<i<<std::endl;

SharedPtr<RowCollection>rows=tables->idx_get(i)->get_Rows();

//Wecanusethe"ToArray"methodonarowcollectiontocloneitintoanarray.
ASPOSE_ASSERT_EQ(rows,rows->ToArray());
ASPOSE_ASSERT_NS(rows,rows->ToArray());

for(intj=0;j<rows->get_Count();j++)
{
std::cout<<"\tStartofRow"<<j<<std::endl;

SharedPtr<CellCollection>cells=rows->idx_get(j)->get_Cells();

//Wecanusethe"ToArray"methodonacellcollectiontocloneitintoanarray.
ASPOSE_ASSERT_EQ(cells,cells->ToArray());
ASPOSE_ASSERT_NS(cells,cells->ToArray());

for(intk=0;k<cells->get_Count();k++)
{
StringcellText=cells->idx_get(k)->ToString(SaveFormat::Text).Trim();
std::cout<<"\t\tContentsofCell:"<<k<<"=\""<<cellText<<"\""<<std::endl;
}

std::cout<<"\tEndofRow"<<j<<std::endl;
}

std::cout<<"EndofTable"<<i<<"\n"<<std::endl;
}

Shows how to build a nested table without using a document builder. voidCreateNestedTable()
{
autodoc=MakeObject<Document>();

//Createtheoutertablewiththreerowsandfourcolumns,andthenaddittothedocument.
SharedPtr<Table>outerTable=CreateTable(doc,3,4,u"OuterTable");
doc->get_FirstSection()->get_Body()->AppendChild(outerTable);

//Createanothertablewithtworowsandtwocolumnsandtheninsertitintothefirsttable'sfirstcell.
SharedPtr<Table>innerTable=CreateTable(doc,2,2,u"InnerTable");
outerTable->get_FirstRow()->get_FirstCell()->AppendChild(innerTable);

doc->Save(ArtifactsDir+u"Table.CreateNestedTable.docx");
}

staticSharedPtr<Table>CreateTable(SharedPtr<Document>doc,introwCount,intcellCount,StringcellText)
{
autotable=MakeObject<Table>(doc);

for(introwId=1;rowId<=rowCount;rowId++)
{
autorow=MakeObject<Row>(doc);
table->AppendChild(row);

for(intcellId=1;cellId<=cellCount;cellId++)
{
autocell=MakeObject<Cell>(doc);
cell->AppendChild(MakeObject<Paragraph>(doc));
cell->get_FirstParagraph()->AppendChild(MakeObject<Run>(doc,cellText));

row->AppendChild(cell);
}
}

//Youcanusethe"Title"and"Description"propertiestoaddatitleanddescriptionrespectivelytoyourtable.
//Thetablemusthaveatleastonerowbeforewecanusetheseproperties.
//ThesepropertiesaremeaningfulforISO/IEC29500compliant.docxdocuments(seetheOoxmlComplianceclass).
//Ifwesavethedocumenttopre-ISO/IEC29500formats,MicrosoftWordignorestheseproperties.
table->set_Title(u"Asposetabletitle");
table->set_Description(u"Asposetabledescription");

returntable;
}
