Examples


Shows how to use a NodeChangingCallback to monitor changes to the document tree in real-time as we edit it. voidNodeChangingCallback()
{
autodoc=MakeObject<Document>();
doc->set_NodeChangingCallback(MakeObject<ExNode::NodeChangingPrinter>());

autobuilder=MakeObject<DocumentBuilder>(doc);
builder->Writeln(u"Helloworld!");
builder->StartTable();
builder->InsertCell();
builder->Write(u"Cell1");
builder->InsertCell();
builder->Write(u"Cell2");
builder->EndTable();

builder->InsertImage(System::Drawing::Image::FromFile(ImageDir+u"Logo.jpg"));

builder->get_CurrentParagraph()->get_ParentNode()->RemoveAllChildren();
}

classNodeChangingPrinter:publicINodeChangingCallback
{
private:
voidNodeInserting(SharedPtr<NodeChangingArgs>args)override
{
ASSERT_EQ(NodeChangingAction::Insert,args->get_Action());
ASPOSE_ASSERT_EQ(nullptr,args->get_OldParent());
}

voidNodeInserted(SharedPtr<NodeChangingArgs>args)override
{
ASSERT_EQ(NodeChangingAction::Insert,args->get_Action());
ASSERT_FALSE(args->get_NewParent()==nullptr);

std::cout<<"Insertednode:"<<std::endl;
std::cout<<String::Format(u"\tType:\t{0}",args->get_Node()->get_NodeType())<<std::endl;

if(args->get_Node()->GetText().Trim()!=u"")
{
std::cout<<"\tText:\t\""<<args->get_Node()->GetText().Trim()<<"\""<<std::endl;
}

std::cout<<"\tHash:\t"<<System::ObjectExt::GetHashCode(args->get_Node())<<std::endl;
std::cout<<String::Format(u"\tParent:\t{0}({1})",args->get_NewParent()->get_NodeType(),System::ObjectExt::GetHashCode(args->get_NewParent()))
<<std::endl;
}

voidNodeRemoving(SharedPtr<NodeChangingArgs>args)override
{
ASSERT_EQ(NodeChangingAction::Remove,args->get_Action());
}

voidNodeRemoved(SharedPtr<NodeChangingArgs>args)override
{
ASSERT_EQ(NodeChangingAction::Remove,args->get_Action());
ASSERT_TRUE(args->get_NewParent()==nullptr);

std::cout<<String::Format(u"Removednode:{0}({1})",args->get_Node()->get_NodeType(),System::ObjectExt::GetHashCode(args->get_Node()))
<<std::endl;
}
};

Examples


Shows how to traverse through a composite node's collection of child nodes. autodoc=MakeObject<Document>();

//Addtworunsandoneshapeaschildnodestothefirstparagraphofthisdocument.
autoparagraph=System::DynamicCast<Paragraph>(doc->GetChild(NodeType::Paragraph,0,true));
paragraph->AppendChild(MakeObject<Run>(doc,u"Helloworld!"));

autoshape=MakeObject<Shape>(doc,ShapeType::Rectangle);
shape->set_Width(200);
shape->set_Height(200);
//Notethatthe'CustomNodeId'isnotsavedtoanoutputfileandexistsonlyduringthenodelifetime.
shape->set_CustomNodeId(100);
shape->set_WrapType(WrapType::Inline);
paragraph->AppendChild(shape);

paragraph->AppendChild(MakeObject<Run>(doc,u"Helloagain!"));

//Iteratethroughtheparagraph'scollectionofimmediatechildren,
//andprintanyrunsorshapesthatwefindwithin.
SharedPtr<NodeCollection>children=paragraph->get_ChildNodes();

ASSERT_EQ(3,paragraph->get_ChildNodes()->get_Count());

for(constauto&child:System::IterateOver(children))
{
switch(child->get_NodeType())
{
caseNodeType::Run:
std::cout<<"Runcontents:"<<std::endl;
std::cout<<"\t\""<<child->GetText().Trim()<<"\""<<std::endl;
break;

caseNodeType::Shape:{
autochildShape=System::DynamicCast<Shape>(child);
std::cout<<"Shape:"<<std::endl;
std::cout<<String::Format(u"\t{0},{1}x{2}",childShape->get_ShapeType(),childShape->get_Width(),childShape->get_Height())<<std::endl;
ASSERT_EQ(100,shape->get_CustomNodeId());
break;
}

default:
break;
}
}

Examples


Shows how to apply custom list formatting to paragraphs when using DocumentBuilder. autodoc=MakeObject<Document>();

//Alistallowsustoorganizeanddecoratesetsofparagraphswithprefixsymbolsandindents.
//Wecancreatenestedlistsbyincreasingtheindentlevel.
//Wecanbeginandendalistbyusingadocumentbuilder's"ListFormat"property.
//Eachparagraphthatweaddbetweenalist'sstartandtheendwillbecomeaniteminthelist.
//CreatealistfromaMicrosoftWordtemplate,andcustomizethefirsttwoofitslistlevels.
SharedPtr<List>list=doc->get_Lists()->Add(ListTemplate::NumberDefault);

SharedPtr<ListLevel>listLevel=list->get_ListLevels()->idx_get(0);
listLevel->get_Font()->set_Color(System::Drawing::Color::get_Red());
listLevel->get_Font()->set_Size(24);
listLevel->set_NumberStyle(NumberStyle::OrdinalText);
listLevel->set_StartAt(21);
listLevel->set_NumberFormat(u"\x0000");

listLevel->set_NumberPosition(-36);
listLevel->set_TextPosition(144);
listLevel->set_TabPosition(144);

listLevel=list->get_ListLevels()->idx_get(1);
listLevel->set_Alignment(ListLevelAlignment::Right);
listLevel->set_NumberStyle(NumberStyle::Bullet);
listLevel->get_Font()->set_Name(u"Wingdings");
listLevel->get_Font()->set_Color(System::Drawing::Color::get_Blue());
listLevel->get_Font()->set_Size(24);

//ThisNumberFormatvaluewillcreatestar-shapedbulletlistsymbols.
listLevel->set_NumberFormat(u"\xf0af");
listLevel->set_TrailingCharacter(ListTrailingCharacter::Space);
listLevel->set_NumberPosition(144);

//Createparagraphsandapplybothlistlevelsofourcustomlistformattingtothem.
autobuilder=MakeObject<DocumentBuilder>(doc);

builder->get_ListFormat()->set_List(list);
builder->Writeln(u"Thequickbrownfox...");
builder->Writeln(u"Thequickbrownfox...");

builder->get_ListFormat()->ListIndent();
builder->Writeln(u"jumpedoverthelazydog.");
builder->Writeln(u"jumpedoverthelazydog.");

builder->get_ListFormat()->ListOutdent();
builder->Writeln(u"Thequickbrownfox...");

builder->get_ListFormat()->RemoveNumbers();

builder->get_Document()->Save(ArtifactsDir+u"Lists.CreateCustomList.docx");

Examples


Shows how to apply and revert page setup settings to sections in a document. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

//Modifythepagesetuppropertiesforthebuilder'scurrentsectionandaddtext.
builder->get_PageSetup()->set_Orientation(Orientation::Landscape);
builder->get_PageSetup()->set_VerticalAlignment(PageVerticalAlignment::Center);
builder->Writeln(u"Thisisthefirstsection,whichlandscapeorientedwithverticallycenteredtext.");

//Ifwestartanewsectionusingadocumentbuilder,
//itwillinheritthebuilder'scurrentpagesetupproperties.
builder->InsertBreak(BreakType::SectionBreakNewPage);

ASSERT_EQ(Orientation::Landscape,doc->get_Sections()->idx_get(1)->get_PageSetup()->get_Orientation());
ASSERT_EQ(PageVerticalAlignment::Center,doc->get_Sections()->idx_get(1)->get_PageSetup()->get_VerticalAlignment());

//Wecanrevertitspagesetuppropertiestotheirdefaultvaluesusingthe"ClearFormatting"method.
builder->get_PageSetup()->ClearFormatting();

ASSERT_EQ(Orientation::Portrait,doc->get_Sections()->idx_get(1)->get_PageSetup()->get_Orientation());
ASSERT_EQ(PageVerticalAlignment::Top,doc->get_Sections()->idx_get(1)->get_PageSetup()->get_VerticalAlignment());

builder->Writeln(u"Thisisthesecondsection,whichisindefaultLetterpapersize,portraitorientationandtopalignment.");

doc->Save(ArtifactsDir+u"PageSetup.ClearFormatting.docx");

