doc->AppendChild(section);

//Setsomepagesetuppropertiesforthesection.
section->get_PageSetup()->set_SectionStart(SectionStart::NewPage);
section->get_PageSetup()->set_PaperSize(PaperSize::Letter);

//Asectionneedsabody,whichwillcontainanddisplayallitscontents
//onthepagebetweenthesection'sheaderandfooter.
autobody=MakeObject<Body>(doc);
section->AppendChild(body);

//Createaparagraph,setsomeformattingproperties,andthenappenditasachildtothebody.
autopara=MakeObject<Paragraph>(doc);

para->get_ParagraphFormat()->set_StyleName(u"Heading1");
para->get_ParagraphFormat()->set_Alignment(ParagraphAlignment::Center);

body->AppendChild(para);

//Finally,addsomecontenttodothedocument.Createarun,
//setitsappearanceandcontents,andthenappenditasachildtotheparagraph.
autorun=MakeObject<Run>(doc);
run->set_Text(u"HelloWorld!");
run->get_Font()->set_Color(System::Drawing::Color::get_Red());
para->AppendChild(run);

ASSERT_EQ(u"HelloWorld!",doc->GetText().Trim());

doc->Save(ArtifactsDir+u"Section.CreateManually.docx");

Examples


Shows how to remove all shapes from a node. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

//UseaDocumentBuildertoinsertashape.Thisisaninlineshape,
//whichhasaparentParagraph,whichisachildnodeofthefirstsection'sBody.
builder->InsertShape(ShapeType::Cube,100.0,100.0);

ASSERT_EQ(1,doc->GetChildNodes(NodeType::Shape,true)->get_Count());

//WecandeleteallshapesfromthechildparagraphsofthisBody.
ASSERT_EQ(StoryType::MainText,doc->get_FirstSection()->get_Body()->get_StoryType());
doc->get_FirstSection()->get_Body()->DeleteShapes();

ASSERT_EQ(0,doc->GetChildNodes(NodeType::Shape,true)->get_Count());

Examples


Shows how to change the style of existing text. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

//Belowaretwowaysofreferencingstyles.
//1-Usingthestylename:
builder->get_Font()->set_StyleName(u"Emphasis");
builder->Writeln(u"Textoriginallyin\"Emphasis\"style");

//2-Usingabuilt-instyleidentifier:
builder->get_Font()->set_StyleIdentifier(StyleIdentifier::IntenseEmphasis);
builder->Writeln(u"Textoriginallyin\"IntenseEmphasis\"style");

//Convertallusesofonestyletoanother,
//usingtheabovemethodstoreferenceoldandnewstyles.
for(constauto&run:System::IterateOver(doc->GetChildNodes(NodeType::Run,true)->LINQ_OfType<SharedPtr<Run>>()))
{
if(run->get_Font()->get_StyleName()==u"Emphasis")
{
run->get_Font()->set_StyleName(u"Strong");
}

if(run->get_Font()->get_StyleIdentifier()==StyleIdentifier::IntenseEmphasis)
{
run->get_Font()->set_StyleIdentifier(StyleIdentifier::Strong);
}
}

doc->Save(ArtifactsDir+u"Font.ChangeStyle.docx");

Examples


Shows how to create a list style and use it in a document. autodoc=MakeObject<Document>();

//Alistallowsustoorganizeanddecoratesetsofparagraphswithprefixsymbolsandindents.
//Wecancreatenestedlistsbyincreasingtheindentlevel.
//Wecanbeginandendalistbyusingadocumentbuilder's"ListFormat"property.
//Eachparagraphthatweaddbetweenalist'sstartandtheendwillbecomeaniteminthelist.
//WecancontainanentireListobjectwithinastyle.
SharedPtr<Style>listStyle=doc->get_Styles()->Add(StyleType::List,u"MyListStyle");

SharedPtr<List>list1=listStyle->get_List();

ASSERT_TRUE(list1->get_IsListStyleDefinition());
ASSERT_FALSE(list1->get_IsListStyleReference());
ASSERT_TRUE(list1->get_IsMultiLevel());
ASPOSE_ASSERT_EQ(listStyle,list1->get_Style());

//Changetheappearanceofalllistlevelsinourlist.
for(constauto&level:list1->get_ListLevels())
{
level->get_Font()->set_Name(u"Verdana");
level->get_Font()->set_Color(System::Drawing::Color::get_Blue());
level->get_Font()->set_Bold(true);
}

autobuilder=MakeObject<DocumentBuilder>(doc);

builder->Writeln(u"Usingliststylefirsttime:");

//Createanotherlistfromalistwithinastyle.
SharedPtr<List>list2=doc->get_Lists()->Add(listStyle);

ASSERT_FALSE(list2->get_IsListStyleDefinition());
ASSERT_TRUE(list2->get_IsListStyleReference());
ASPOSE_ASSERT_EQ(listStyle,list2->get_Style());

//Addsomelistitemsthatourlistwillformat.
builder->get_ListFormat()->set_List(list2);
builder->Writeln(u"Item1");
builder->Writeln(u"Item2");
builder->get_ListFormat()->RemoveNumbers();

builder->Writeln(u"Usingliststylesecondtime:");

//Createandapplyanotherlistbasedontheliststyle.
SharedPtr<List>list3=doc->get_Lists()->Add(listStyle);
builder->get_ListFormat()->set_List(list3);
builder->Writeln(u"Item1");
builder->Writeln(u"Item2");
builder->get_ListFormat()->RemoveNumbers();

builder->get_Document()->Save(ArtifactsDir+u"Lists.CreateAndUseListStyle.docx");

Examples


Shows how to build a formatted 2x2 table. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

SharedPtr<Table>table=builder->StartTable();
builder->InsertCell();
builder->get_CellFormat()->set_VerticalAlignment(CellVerticalAlignment::Center);
builder->Write(u"Row1,cell1.");
builder->InsertCell();
builder->Write(u"Row1,cell2.");
builder->EndRow();

//Whilebuildingthetable,thedocumentbuilderwillapplyitscurrentRowFormat/CellFormatpropertyvalues
//tothecurrentrow/cellthatitscursorisinandanynewrows/cellsasitcreatesthem.
ASSERT_EQ(CellVerticalAlignment::Center,table->get_Rows()->idx_get(0)->get_Cells()->idx_get(0)->get_CellFormat()->get_VerticalAlignment());
ASSERT_EQ(CellVerticalAlignment::Center,table->get_Rows()->idx_get(0)->get_Cells()->idx_get(1)->get_CellFormat()->get_VerticalAlignment());

builder->InsertCell();
builder->get_RowFormat()->set_Height(100);
builder->get_RowFormat()->set_HeightRule(HeightRule::Exactly);
builder->get_CellFormat()->set_Orientation(TextOrientation::Upward);
builder->Write(u"Row2,cell1.");
builder->InsertCell();
builder->get_CellFormat()->set_Orientation(TextOrientation::Downward);
builder->Write(u"Row2,cell2.");
builder->EndRow();
builder->EndTable();

//Previouslyaddedrowsandcellsarenotretroactivelyaffectedbychangestothebuilder'sformatting.
ASPOSE_ASSERT_EQ(0,table->get_Rows()->idx_get(0)->get_RowFormat()->get_Height());
ASSERT_EQ(HeightRule::Auto,table->get_Rows()->idx_get(0)->get_RowFormat()->get_HeightRule());
ASPOSE_ASSERT_EQ(100,table->get_Rows()->idx_get(1)->get_RowFormat()->get_Height());
ASSERT_EQ(HeightRule::Exactly,table->get_Rows()->idx_get(1)->get_RowFormat()->get_HeightRule());
ASSERT_EQ(TextOrientation::Upward,table->get_Rows()->idx_get(1)->get_Cells()->idx_get(0)->get_CellFormat()->get_Orientation());
ASSERT_EQ(TextOrientation::Downward,table->get_Rows()->idx_get(1)->get_Cells()->idx_get(1)->get_CellFormat()->get_Orientation());

doc->Save(ArtifactsDir+u"DocumentBuilder.BuildTable.docx");

