Examples


Shows how to set the property for finding the closest match for a missing font from the available font sources. voidEnableFontSubstitution()
{
//Openadocumentthatcontainstextformattedwithafontthatdoesnotexistinanyofourfontsources.
autodoc=MakeObject<Document>(MyDir+u"Missingfont.docx");

//Assignacallbackforhandlingfontsubstitutionwarnings.
autosubstitutionWarningHandler=MakeObject<ExFontSettings::HandleDocumentSubstitutionWarnings>();
doc->set_WarningCallback(substitutionWarningHandler);

//Setadefaultfontnameandenablefontsubstitution.
autofontSettings=MakeObject<FontSettings>();
fontSettings->get_SubstitutionSettings()->get_DefaultFontSubstitution()->set_DefaultFontName(u"Arial");
;
fontSettings->get_SubstitutionSettings()->get_FontInfoSubstitution()->set_Enabled(true);

//Wewillgetafontsubstitutionwarningifwesaveadocumentwithamissingfont.
doc->set_FontSettings(fontSettings);
doc->Save(ArtifactsDir+u"FontSettings.EnableFontSubstitution.pdf");

{
SharedPtr<System::Collections::Generic::IEnumerator<SharedPtr<WarningInfo>>>warnings=substitutionWarningHandler->FontWarnings->GetEnumerator();
while(warnings->MoveNext())
{
std::cout<<warnings->get_Current()->get_Description()<<std::endl;
}
}

//Wecanalsoverifywarningsinthecollectionandclearthem.
ASSERT_EQ(WarningSource::Layout,substitutionWarningHandler->FontWarnings->idx_get(0)->get_Source());
ASSERT_EQ(u"Font'28DaysLater'hasnotbeenfound.Using'Calibri'fontinstead.Reason:alternativenamefromdocument.",
substitutionWarningHandler->FontWarnings->idx_get(0)->get_Description());

substitutionWarningHandler->FontWarnings->Clear();

ASSERT_EQ(0,substitutionWarningHandler->FontWarnings->get_Count());
}

classHandleDocumentSubstitutionWarnings:publicIWarningCallback
{
public:
SharedPtr<WarningInfoCollection>FontWarnings;

voidWarning(SharedPtr<WarningInfo>info)override
{
if(info->get_WarningType()==WarningType::FontSubstitution)
{
FontWarnings->Warning(info);
}
}

HandleDocumentSubstitutionWarnings():FontWarnings(MakeObject<WarningInfoCollection>())
{
}
};

Examples


Shows how to create a text watermark. autodoc=MakeObject<Document>();

//Addaplaintextwatermark.
doc->get_Watermark()->SetText(u"AsposeWatermark");

//Ifwewishtoeditthetextformattingusingitasawatermark,
//wecandosobypassingaTextWatermarkOptionsobjectwhencreatingthewatermark.
autotextWatermarkOptions=MakeObject<TextWatermarkOptions>();
textWatermarkOptions->set_FontFamily(u"Arial");
textWatermarkOptions->set_FontSize(36.0f);
textWatermarkOptions->set_Color(System::Drawing::Color::get_Black());
textWatermarkOptions->set_Layout(WatermarkLayout::Diagonal);
textWatermarkOptions->set_IsSemitrasparent(false);

doc->get_Watermark()->SetText(u"AsposeWatermark",textWatermarkOptions);

doc->Save(ArtifactsDir+u"Document.TextWatermark.docx");

//Wecanremoveawatermarkfromadocumentlikethis.
if(doc->get_Watermark()->get_Type()==WatermarkType::Text)
{
doc->get_Watermark()->Remove();
}

