ASSERT_EQ(u"Series1",chart->get_Series()->idx_get(0)->get_Name());
ASSERT_EQ(u"Series2",chart->get_Series()->idx_get(1)->get_Name());
ASSERT_EQ(u"Series3",chart->get_Series()->idx_get(2)->get_Name());

//Movethechart'slegendtothetoprightcorner.
SharedPtr<ChartLegend>legend=chart->get_Legend();
legend->set_Position(LegendPosition::TopRight);

//Giveotherchartelements,suchasthegraph,moreroombyallowingthemtooverlapthelegend.
legend->set_Overlay(true);

doc->Save(ArtifactsDir+u"Charts.ChartLegend.docx");

