Shows how to work with data points on a line chart. voidChartDataPoint_()
{
autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

SharedPtr<Shape>shape=builder->InsertChart(ChartType::Line,500,350);
SharedPtr<Chart>chart=shape->get_Chart();

ASSERT_EQ(3,chart->get_Series()->get_Count());
ASSERT_EQ(u"Series1",chart->get_Series()->idx_get(0)->get_Name());
ASSERT_EQ(u"Series2",chart->get_Series()->idx_get(1)->get_Name());
ASSERT_EQ(u"Series3",chart->get_Series()->idx_get(2)->get_Name());

//Emphasizethechart'sdatapointsbymakingthemappearasdiamondshapes.
for(constauto&series:System::IterateOver(chart->get_Series()))
{
ApplyDataPoints(series,4,MarkerSymbol::Diamond,15);
}

//Smoothoutthelinethatrepresentsthefirstdataseries.
chart->get_Series()->idx_get(0)->set_Smooth(true);

//Verifythatdatapointsforthefirstserieswillnotinverttheircolorsifthevalueisnegative.
{
SharedPtr<System::Collections::Generic::IEnumerator<SharedPtr<ChartDataPoint>>>enumerator=
chart->get_Series()->idx_get(0)->get_DataPoints()->GetEnumerator();
while(enumerator->MoveNext())
{
ASSERT_FALSE(enumerator->get_Current()->get_InvertIfNegative());
}
}

//Foracleanerlookinggraph,wecanclearformatindividually.
chart->get_Series()->idx_get(1)->get_DataPoints()->idx_get(2)->ClearFormat();

//Wecanalsostripanentireseriesofdatapointsatonce.
chart->get_Series()->idx_get(2)->get_DataPoints()->ClearFormat();

doc->Save(ArtifactsDir+u"Charts.ChartDataPoint.docx");
}

staticvoidApplyDataPoints(SharedPtr<ChartSeries>series,intdataPointsCount,MarkerSymbolmarkerSymbol,intdataPointSize)
{
for(inti=0;i<dataPointsCount;i++)
{
SharedPtr<ChartDataPoint>point=series->get_DataPoints()->idx_get(i);
point->get_Marker()->set_Symbol(markerSymbol);
point->get_Marker()->set_Size(dataPointSize);

ASSERT_EQ(i,point->get_Index());
}
}
