Examples


Shows how to insert an IF field. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

builder->Write(u"Statement1:");
autofield=System::DynamicCast<FieldIf>(builder->InsertField(FieldType::FieldIf,true));
field->set_LeftExpression(u"0");
field->set_ComparisonOperator(u"=");
field->set_RightExpression(u"1");

//TheIFfieldwilldisplayastringfromeitherits"TrueText"property,
//orits"FalseText"property,dependingonthetruthofthestatementthatwehaveconstructed.
field->set_TrueText(u"True");
field->set_FalseText(u"False");
field->Update();

//Inthiscase,"0=1"isincorrect,sothedisplayedresultwillbe"False".
ASSERT_EQ(u"IF0=1TrueFalse",field->GetFieldCode());
ASSERT_EQ(FieldIfComparisonResult::False,field->EvaluateCondition());
ASSERT_EQ(u"False",field->get_Result());

builder->Write(u"\nStatement2:");
field=System::DynamicCast<FieldIf>(builder->InsertField(FieldType::FieldIf,true));
field->set_LeftExpression(u"5");
field->set_ComparisonOperator(u"=");
field->set_RightExpression(u"2+3");
field->set_TrueText(u"True");
field->set_FalseText(u"False");
field->Update();

//Thistimethestatementiscorrect,sothedisplayedresultwillbe"True".
ASSERT_EQ(u"IF5=\"2+3\"TrueFalse",field->GetFieldCode());
ASSERT_EQ(FieldIfComparisonResult::True,field->EvaluateCondition());
ASSERT_EQ(u"True",field->get_Result());

doc->UpdateFields();
doc->Save(ArtifactsDir+u"Field.IF.docx");

Examples


Shows how to insert a field into a document using a field code. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

SharedPtr<Field>field=builder->InsertField(u"DATE\\@\"dddd,MMMMdd,yyyy\"");

ASSERT_EQ(FieldType::FieldDate,field->get_Type());
ASSERT_EQ(u"DATE\\@\"dddd,MMMMdd,yyyy\"",field->GetFieldCode());

//ThisoverloadoftheInsertFieldmethodautomaticallyupdatesinsertedfields.
ASSERT_LE(System::Math::Abs((System::DateTime::Parse(field->get_Result())-System::DateTime::get_Today()).get_Hours()),24);

Shows how to work with a FieldStart node. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

autofield=System::DynamicCast<FieldDate>(builder->InsertField(FieldType::FieldDate,true));
field->get_Format()->set_DateTimeFormat(u"dddd,MMMMdd,yyyy");
field->Update();

SharedPtr<FieldChar>fieldStart=field->get_Start();

ASSERT_EQ(FieldType::FieldDate,fieldStart->get_FieldType());
ASPOSE_ASSERT_EQ(false,fieldStart->get_IsDirty());
ASPOSE_ASSERT_EQ(false,fieldStart->get_IsLocked());

//Retrievethefacadeobjectwhichrepresentsthefieldinthedocument.
field=System::DynamicCast<FieldDate>(fieldStart->GetField());

ASPOSE_ASSERT_EQ(false,field->get_IsLocked());
ASSERT_EQ(u"DATE\\@\"dddd,MMMMdd,yyyy\"",field->GetFieldCode());

//Updatethefieldtoshowthecurrentdate.
field->Update();

Examples


Shows how to format field results. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

//Useadocumentbuildertoinsertafieldthatdisplaysaresultwithnoformatapplied.
SharedPtr<Field>field=builder->InsertField(u"=2+3");

ASSERT_EQ(u"=2+3",field->GetFieldCode());
ASSERT_EQ(u"5",field->get_Result());

//Wecanapplyaformattoafield'sresultusingthefield'sproperties.
//Belowarethreetypesofformatsthatwecanapplytoafield'sresult.
//1-Numericformat:
SharedPtr<FieldFormat>format=field->get_Format();
format->set_NumericFormat(u"$###.00");
field->Update();

ASSERT_EQ(u"=2+3\\#$###.00",field->GetFieldCode());
ASSERT_EQ(u"$5.00",field->get_Result());

//2-Date/timeformat:
field=builder->InsertField(u"DATE");
format=field->get_Format();
format->set_DateTimeFormat(u"dddd,MMMMdd,yyyy");
field->Update();

ASSERT_EQ(u"DATE\\@\"dddd,MMMMdd,yyyy\"",field->GetFieldCode());
std::cout<<"Today'sdate,in"<<format->get_DateTimeFormat()<<"format:\n\t"<<field->get_Result()<<std::endl;

//3-Generalformat:
field=builder->InsertField(u"=25+33");
format=field->get_Format();
format->get_GeneralFormats()->Add(GeneralFormat::LowercaseRoman);
format->get_GeneralFormats()->Add(GeneralFormat::Upper);
field->Update();

intindex=0;
{
SharedPtr<System::Collections::Generic::IEnumerator<GeneralFormat>>generalFormatEnumerator=format->get_GeneralFormats()->GetEnumerator();
while(generalFormatEnumerator->MoveNext())
{
std::cout<<String::Format(u"Generalformatindex{0}:{1}",index++,generalFormatEnumerator->get_Current())<<std::endl;
}
}

ASSERT_EQ(u"=25+33\\*roman\\*Upper",field->GetFieldCode());
ASSERT_EQ(u"LVIII",field->get_Result());
ASSERT_EQ(2,format->get_GeneralFormats()->get_Count());
ASSERT_EQ(GeneralFormat::LowercaseRoman,format->get_GeneralFormats()->idx_get(0));

//Wecanremoveourformatstorevertthefield'sresulttoitsoriginalform.
format->get_GeneralFormats()->Remove(GeneralFormat::LowercaseRoman);
format->get_GeneralFormats()->RemoveAt(0);
ASSERT_EQ(0,format->get_GeneralFormats()->get_Count());
field->Update();

ASSERT_EQ(u"=25+33",field->GetFieldCode());
ASSERT_EQ(u"58",field->get_Result());
ASSERT_EQ(0,format->get_GeneralFormats()->get_Count());

Examples


Shows how to create form fields. autobuilder=MakeObject<DocumentBuilder>();

//Formfieldsareobjectsinthedocumentthattheusercaninteractwithbybeingpromptedtoentervalues.
//Wecancreatethemusingadocumentbuilder,andbelowaretwowaysofdoingso.
//1-Basictextinput:
builder->InsertTextInput(u"Mytextinput",TextFormFieldType::Regular,u"",u"Enteryournamehere",30);

//2-Comboboxwithprompttext,andarangeofpossiblevalues:
ArrayPtr<String>items=MakeArray<String>({u"--Selectyourfavoritefootwear--",u"Sneakers",u"Oxfords",u"Flip-flops",u"Other"});

builder->InsertParagraph();
builder->InsertComboBox(u"Mycombobox",items,0);

builder->get_Document()->Save(ArtifactsDir+u"DocumentBuilder.CreateForm.docx");
