Examples


Shows how to trim whitespace when loading plaintext documents. StringtextDoc=String(u"Line1\n")+u"Line2\n"+u"Line3";

//Createa"TxtLoadOptions"object,whichwecanpasstoadocument'sconstructor
//tomodifyhowweloadaplaintextdocument.
autoloadOptions=MakeObject<TxtLoadOptions>();

//Setthe"LeadingSpacesOptions"propertyto"TxtLeadingSpacesOptions.Preserve"
//topreserveallwhitespacecharactersatthestartofeveryline.
//Setthe"LeadingSpacesOptions"propertyto"TxtLeadingSpacesOptions.ConvertToIndent"
//toremoveallwhitespacecharactersfromthestartofeveryline,
//andthenapplyaleftfirstlineindenttotheparagraphtosimulatetheeffectofthewhitespaces.
//Setthe"LeadingSpacesOptions"propertyto"TxtLeadingSpacesOptions.Trim"
//toremoveallwhitespacecharactersfromeveryline'sstart.
loadOptions->set_LeadingSpacesOptions(txtLeadingSpacesOptions);

//Setthe"TrailingSpacesOptions"propertyto"TxtTrailingSpacesOptions.Preserve"
//topreserveallwhitespacecharactersattheendofeveryline.
//Setthe"TrailingSpacesOptions"propertyto"TxtTrailingSpacesOptions.Trim"to
//removeallwhitespacecharactersfromtheendofeveryline.
loadOptions->set_TrailingSpacesOptions(txtTrailingSpacesOptions);

autodoc=MakeObject<Document>(MakeObject<System::IO::MemoryStream>(System::Text::Encoding::get_UTF8()->GetBytes(textDoc)),loadOptions);
SharedPtr<ParagraphCollection>paragraphs=doc->get_FirstSection()->get_Body()->get_Paragraphs();

switch(txtLeadingSpacesOptions)
{
caseTxtLeadingSpacesOptions::ConvertToIndent:
ASPOSE_ASSERT_EQ(37.8,paragraphs->idx_get(0)->get_ParagraphFormat()->get_FirstLineIndent());
ASPOSE_ASSERT_EQ(25.2,paragraphs->idx_get(1)->get_ParagraphFormat()->get_FirstLineIndent());
ASPOSE_ASSERT_EQ(6.3,paragraphs->idx_get(2)->get_ParagraphFormat()->get_FirstLineIndent());
ASSERT_TRUE(paragraphs->idx_get(0)->GetText().StartsWith(u"Line1"));
ASSERT_TRUE(paragraphs->idx_get(1)->GetText().StartsWith(u"Line2"));
ASSERT_TRUE(paragraphs->idx_get(2)->GetText().StartsWith(u"Line3"));
break;

caseTxtLeadingSpacesOptions::Preserve:
ASSERT_TRUE(paragraphs->LINQ_All([](SharedPtr<Node>p)
{return(System::DynamicCast<Paragraph>(p))->get_ParagraphFormat()->get_FirstLineIndent()==0.0;}));
ASSERT_TRUE(paragraphs->idx_get(0)->GetText().StartsWith(u"Line1"));
ASSERT_TRUE(paragraphs->idx_get(1)->GetText().StartsWith(u"Line2"));
ASSERT_TRUE(paragraphs->idx_get(2)->GetText().StartsWith(u"Line3"));
break;

caseTxtLeadingSpacesOptions::Trim:
ASSERT_TRUE(paragraphs->LINQ_All([](SharedPtr<Node>p)
{return(System::DynamicCast<Paragraph>(p))->get_ParagraphFormat()->get_FirstLineIndent()==0.0;}));
ASSERT_TRUE(paragraphs->idx_get(0)->GetText().StartsWith(u"Line1"));
ASSERT_TRUE(paragraphs->idx_get(1)->GetText().StartsWith(u"Line2"));
ASSERT_TRUE(paragraphs->idx_get(2)->GetText().StartsWith(u"Line3"));
break;
}

switch(txtTrailingSpacesOptions)
{
caseTxtTrailingSpacesOptions::Preserve:
ASSERT_TRUE(paragraphs->idx_get(0)->GetText().EndsWith(u"Line1\r"));
ASSERT_TRUE(paragraphs->idx_get(1)->GetText().EndsWith(u"Line2\r"));
ASSERT_TRUE(paragraphs->idx_get(2)->GetText().EndsWith(u"Line3\f"));
break;

caseTxtTrailingSpacesOptions::Trim:
ASSERT_TRUE(paragraphs->idx_get(0)->GetText().EndsWith(u"Line1\r"));
ASSERT_TRUE(paragraphs->idx_get(1)->GetText().EndsWith(u"Line2\r"));
ASSERT_TRUE(paragraphs->idx_get(2)->GetText().EndsWith(u"Line3\f"));
break;
}

