Examples


Shows how to work with CSS stylesheets that an HTML conversion creates. voidExternalCssFilenames()
{
autodoc=MakeObject<Document>(MyDir+u"Rendering.docx");

//Createan"HtmlFixedSaveOptions"object,whichwecanpasstothedocument's"Save"method
//tomodifyhowweconvertthedocumenttoHTML.
autooptions=MakeObject<HtmlSaveOptions>();

//Setthe"CssStylesheetType"propertyto"CssStyleSheetType.External"to
//accompanyasavedHTMLdocumentwithanexternalCSSstylesheetfile.
options->set_CssStyleSheetType(CssStyleSheetType::External);

//BelowaretwowaysofspecifyingdirectoriesandfilenamesforoutputCSSstylesheets.
//1-Usethe"CssStyleSheetFileName"propertytoassignafilenametoourstylesheet:
options->set_CssStyleSheetFileName(ArtifactsDir+u"SavingCallback.ExternalCssFilenames.css");

//2-Useacustomcallbacktonameourstylesheet:
options->set_CssSavingCallback(
MakeObject<ExSavingCallback::CustomCssSavingCallback>(ArtifactsDir+u"SavingCallback.ExternalCssFilenames.css",true,false));

doc->Save(ArtifactsDir+u"SavingCallback.ExternalCssFilenames.html",options);
}

classCustomCssSavingCallback:publicICssSavingCallback
{
public:
CustomCssSavingCallback(StringcssDocFilename,boolisExportNeeded,boolkeepCssStreamOpen):mIsExportNeeded(false),mKeepCssStreamOpen(false)
{
mCssTextFileName=cssDocFilename;
mIsExportNeeded=isExportNeeded;
mKeepCssStreamOpen=keepCssStreamOpen;
}

voidCssSaving(SharedPtr<CssSavingArgs>args)override
{
//Wecanaccesstheentiresourcedocumentviathe"Document"property.
ASSERT_TRUE(args->get_Document()->get_OriginalFileName().EndsWith(u"Rendering.docx"));

args->set_CssStream(MakeObject<System::IO::FileStream>(mCssTextFileName,System::IO::FileMode::Create));
args->set_IsExportNeeded(mIsExportNeeded);
args->set_KeepCssStreamOpen(mKeepCssStreamOpen);

ASSERT_TRUE(args->get_CssStream()->get_CanWrite());
}

private:
StringmCssTextFileName;
boolmIsExportNeeded;
boolmKeepCssStreamOpen;
};

