builder->Writeln(u"Outlinelegalheadinglistitem5.");
builder->get_ListFormat()->RemoveNumbers();

//WhensavingthedocumenttoHTML,wecanpassaSaveOptionsobject
//todecidewhichHTMLelementsthedocumentwillusetorepresentlists.
//Settingthe"ExportListLabels"propertyto"ExportListLabels.AsInlineText"
//willcreatelistsbyformattingspans.
//Settingthe"ExportListLabels"propertyto"ExportListLabels.Auto"willusethe<p>tag
//tobuildlistsincaseswhenusingthe<ol>and<li>tagsmaycauselossofformatting.
//Settingthe"ExportListLabels"propertyto"ExportListLabels.ByHtmlTags"
//willuse<ol>and<li>tagstobuildalllists.
autooptions=MakeObject<HtmlSaveOptions>();
options->set_ExportListLabels(exportListLabels);

doc->Save(ArtifactsDir+u"HtmlSaveOptions.List.html",options);
StringoutDocContents=System::IO::File::ReadAllText(ArtifactsDir+u"HtmlSaveOptions.List.html");

switch(exportListLabels)
{
caseExportListLabels::AsInlineText:
ASSERT_TRUE(outDocContents.Contains(
String(u"<pstyle=\"margin-top:0pt;margin-left:72pt;margin-bottom:0pt;text-indent:-18pt;-aw-import:list-item;-aw-list-level-number:1;"
u"-aw-list-number-format:'%1.';-aw-list-number-styles:'lowerLetter';-aw-list-number-values:'1';-aw-list-padding-sml:9.67pt\">")+
u"<spanstyle=\"-aw-import:ignore\">"+u"<span>a.</span>"+
u"<spanstyle=\"width:9.67pt;font:7pt'TimesNewRoman';display:inline-block;-aw-import:spaces\">&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;"
u"</span>"+
u"</span>"+u"<span>Defaultnumberedlistitem3.</span>"+u"</p>"));
ASSERT_TRUE(
outDocContents.Contains(String(u"<pstyle=\"margin-top:0pt;margin-left:43.2pt;margin-bottom:0pt;text-indent:-43.2pt;-aw-import:list-item;"
u"-aw-list-level-number:3;-aw-list-number-format:'%0.%1.%2.%3';-aw-list-number-styles:'decimaldecimal"
u"decimaldecimal';-aw-list-number-values:'2111';-aw-list-padding-sml:10.2pt\">")+
u"<spanstyle=\"-aw-import:ignore\">"+u"<span>2.1.1.1</span>"+
u"<spanstyle=\"width:10.2pt;font:7pt'TimesNewRoman';display:inline-block;"
u"-aw-import:spaces\">&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;</span>"+
u"</span>"+u"<span>Outlinelegalheadinglistitem5.</span>"+u"</p>"));
break;

caseExportListLabels::Auto:
ASSERT_TRUE(outDocContents.Contains(String(u"<oltype=\"a\"style=\"margin-right:0pt;margin-left:0pt;padding-left:0pt\">")+
u"<listyle=\"margin-left:31.33pt;padding-left:4.67pt\">"+u"<span>Defaultnumberedlistitem3.</span>"+
u"</li>"+u"</ol>"));
ASSERT_TRUE(outDocContents.Contains(
String(
u"<pstyle=\"margin-top:0pt;margin-left:43.2pt;margin-bottom:0pt;text-indent:-43.2pt;-aw-import:list-item;-aw-list-level-number:3;")+
u"-aw-list-number-format:'%0.%1.%2.%3';-aw-list-number-styles:'decimaldecimaldecimaldecimal';"+
u"-aw-list-number-values:'2111';-aw-list-padding-sml:10.2pt\">"+u"<spanstyle=\"-aw-import:ignore\">"+u"<span>2.1.1.1</span>"+
u"<spanstyle=\"width:10.2pt;font:7pt'TimesNewRoman';display:inline-block;-aw-import:spaces\">&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;"
u"</span>"+
u"</span>"+u"<span>Outlinelegalheadinglistitem5.</span>"+u"</p>"));
break;

caseExportListLabels::ByHtmlTags:
ASSERT_TRUE(outDocContents.Contains(String(u"<oltype=\"a\"style=\"margin-right:0pt;margin-left:0pt;padding-left:0pt\">")+
u"<listyle=\"margin-left:31.33pt;padding-left:4.67pt\">"+u"<span>Defaultnumberedlistitem3.</span>"+
u"</li>"+u"</ol>"));
ASSERT_TRUE(outDocContents.Contains(String(u"<oltype=\"1\"class=\"awlist3\"style=\"margin-right:0pt;margin-left:0pt;padding-left:0pt\">")+
u"<listyle=\"margin-left:7.2pt;text-indent:-43.2pt;-aw-list-padding-sml:10.2pt\">"+
u"<spanstyle=\"width:10.2pt;font:7pt'TimesNewRoman';display:inline-block;"
u"-aw-import:ignore\">&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;</span>"+
u"<span>Outlinelegalheadinglistitem5.</span>"+u"</li>"+u"</ol>"));
break;
}

