Shows how to preserve negative indents in the output .html. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

//Insertatablewithanegativeindent,whichwillpushittotheleftpasttheleftpageboundary.
SharedPtr<Table>table=builder->StartTable();
builder->InsertCell();
builder->Write(u"Row1,Cell1");
builder->InsertCell();
builder->Write(u"Row1,Cell2");
builder->EndTable();
table->set_LeftIndent(-36);
table->set_PreferredWidth(PreferredWidth::FromPoints(144));

builder->InsertBreak(BreakType::ParagraphBreak);

//Insertatablewithapositiveindent,whichwillpushthetabletotheright.
table=builder->StartTable();
builder->InsertCell();
builder->Write(u"Row1,Cell1");
builder->InsertCell();
builder->Write(u"Row1,Cell2");
builder->EndTable();
table->set_LeftIndent(36);
table->set_PreferredWidth(PreferredWidth::FromPoints(144));

//WhenwesaveadocumenttoHTML,Aspose.Wordswillonlypreservenegativeindents
//suchastheonewehaveappliedtothefirsttableifwesetthe"AllowNegativeIndent"flag
//inaSaveOptionsobjectthatwewillpassto"true".
