u"<!DOCTYPEhtml\r\nPUBLIC\"-//W3C//DTDXHTML1.0Transitional//EN\"\r\n"
u"\"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\r\n"+
u"<htmlxmlns=\"http://www.w3.org/1999/xhtml\">"));
}
else
{
ASSERT_TRUE(outDocContents.Contains(u"<html>"));
}

